INSERT INTO `poem_table` (`poem_id`, `poem_name`, `poem_content`, `poem_author`) VALUES
(56, 'tương quan lực lượng', '<div class="col-md-12">&#13;\n        Đám dân chủ chưa thành một lực lượng<br/>Còn rã rời nhưng phát biểu linh tinh<br/>Chỉ cô lập là hết phương cọ nguậy<br/>Cờ trong tay để đảng phất một mình !?<br/><br/>Đảng cứ thế độc đàn xưng bá chủ<br/>Vẫn tôn thờ thánh Mác với Lê Nin<br/>Làm chính trị mỗi ngày thêm buồn ngủ<br/>Chỉ lo thân việc nước hóa vô tình ?<br/><br/>Vài phe nhóm Cộng sản thường gờm sợ<br/>Như Thiên chúa  xuất xứ tự Rô ma<br/>Có tổ chức và tứ phương bảo trợ<br/>Vẫn thường xuyên so kiếm với đảng ta ...<br/><br/>Đảng vẫn mạnh ưu thế hơn tất cả<br/>Nắm quyền hành sinh sát ở trong tay<br/>Công an trị vẫn như đường lối cũ<br/>Hễ cản đường xe húc chết kệ bây !<br/><br/>Đảng tiếp tục giữ vai trò lãnh đạo<br/>Chẳng còn là đày tớ của nhân dân<br/>Vì thẳng thắng vẫn hay hơn ba xạo<br/>Súng trong tay ai nỡ bỏ không dùng !?<br/><br/>Tiếp tục đẩy nước non vào xây dựng<br/>Mặc môi sinh thảm hoạ đã tràn lan<br/>Như hậu quả đương nhiên dân phải hứng ?<br/>Cho xoáy cong Cộng sản đến thiên đàng !?<br/><br/>Đảng hi vọng thời gian là liều thuốc<br/>Như bao năm dưới chế độ độc tài<br/>Kỷ luật sắt đưa nước về phía trước<br/>Một ngày kia lịch sử sẽ vỗ tay ?<br/><br/>Nhưng ai biết chuyện gì đang xảy đến ?<br/>Trong thâm sâu vận chuyển cỗ máy trời<br/>Đùng một cái đảng ta thành rã đám<br/>Như bao đời bạo chúa khắp mọi nơi ?<br/><br/>QHNC&#13;\n      </div>', 'Quoc Hung Nguyen Cao'),
(57, 'nhớ ơn cha mẹ vô vàn thiết tha 1', '<div class="col-md-12">&#13;\n        NHỚ ƠN CHA MẸ VÔ VÀN THIẾT THA<br/>Con quỳ lạy Phật từ bi<br/>Cho con trí huệ bước đi đường đời<br/>Nhẹ nhàng tâm thức thảnh thơi<br/>Điều hay lẽ phải những lời yêu thương<br/>Nghe trong ngày tháng vô thường<br/>Lời kinh bát nhã vô lường nhân sinh<br/>Trôi lăn trong cõi hữu tình<br/>Lòng con hạnh phúc tâm mình hân hoan<br/>Bình minh rạng rỡ huy hoàng<br/>Nhớ ơn Cha Mẹ vô vàn thiết tha<br/>trần minh hiền orlando ngày 6 tháng 8 năm 2014<br/>UNTITLED 08062014<br/>I have been learning from my parents, my teachers and my Buddha daily<br/>I understand the deep cause of our misery<br/>That helps me staying happy<br/>Thanks to my parents’ sacrifices and dedication, I can reach the infinity<br/>trần minh hiền orlando August 6, 2014&#13;\n      </div>', 'Trần Minh Hiền'),
(58, 'người âm thầm hy sinh', '<div class="col-md-12">&#13;\n        NGƯỜI ÂM THẦM HY SINH<br/>( Kính tặng Bác Nhạc Sĩ LMST và cô Hoàng Yến)<br/>người nhạc sĩ Si Tình<br/>rung phím đàn Hoàng Yến<br/>trong căn nhà thanh tịnh<br/>bên biển đời thần tiên<br/><br/>ôi tình yêu rất hiền<br/>trăm năm còn xanh mãi<br/>vầng trăng rằm hiển hiện<br/>sáng cuộc đời tương lai<br/><br/>đêm từng đêm miệt mài<br/>nâng niu từng nốt nhạc<br/>chắt chiu ngàn cung bậc<br/>đẹp tâm hồn trúc mai<br/><br/>dòng thời gian rất dài<br/>tình vợ chồng đẹp mãi<br/>người trọn đời vun vén<br/>người âm thầm hy sinh<br/>trần minh hiền orlando ngày 27 tháng 3 năm 2015<br/>PERFECT RHYME<br/>( For great musician LMST and his even greater person, his wife, Mrs. Hoang Yen)<br/>There is a great musician who sacrifices his life to music<br/>and there is a quiet woman supports him all the time<br/>under her care, everything becomes magic<br/>and then all of the songs become perfect rhyme<br/>trần minh hiền orlando ngày 27 tháng 3 năm 2015&#13;\n      </div>', 'Trần Minh Hiền'),
(59, 'ban mai trinh', '<div class="col-md-12">&#13;\n        BAN MAI TRINH<br/><br/>Ngắm đóa hoa tươi chợt giật mình<br/>Ô hầy giống bậu thật là xinh<br/>Hương thơm ngát quyện hình ong bướm<br/>Sắc đượm loang vời sóng lân tinh<br/>Ảo huyễn  bên đời tình bén nở<br/>Huyền u trước cảnh ái đan trình<br/>Ban mai khiết - tịnh lung linh thở<br/>Vẻ đẹp thêm hừng của gái trinh...<br/><br/>10-02-2016<br/>Nguyên Hữu&#13;\n      </div>', 'Nguyên Hữu'),
(60, 'cung thương', '<div class="col-md-12">&#13;\n        Nhè nhẹ rung lay động phím tơ tình <br/>Du dương hát rung rinh niềm sâu lắng <br/>Đêm thờ thẩn những canh dài vắng lặng <br/>Ta buông lơi một áng nhạc tình ca<br/><br/>Dạ cô sầu ... ôm giấc mộng mờ xa <br/>Mùi hương ái  mặn mà đầy nỗi nhớ <br/>Đôi môi nát còn đâu duyên tình nợ <br/>Tình ơi tình !... bỡ ngỡ đến hư hao<br/><br/>Trong đêm dài hồn cháy đến nao nao<br/>Lời thơ vọng nơi nào anh có biết<br/>Để nỗi nhớ trong lòng em da diết<br/>Đam mê ơi !... tha thiết đến vô bờ <br/><br/>Trong cô đơn khắc khoải nhớ vần thơ<br/>Trong khoảng trống ta chờ trong vô vọng<br/>Nghe tiếng hát  ... thả hồn trong mơ mộng<br/>Bóng nguyệt tàn  rung động lá vàng rơi…<br/><br/>Ngẫm nghỉ suy… ồ !...đã hết nửa đời<br/>Sầu rơi rớt rã rời theo cung bậc<br/>Ta hốt hoảng khóc lên trong cơn nấc<br/>Thả hồn đâu khổ thật ải dương trần <br/><br/>Tiếng đàn ngân rung động đến cung vân<br/>Sương ướt đẫm  ngại ngần dòng thu nguyệt<br/>Em muốn đến ngụ lầu thơ diễm tuyệt<br/>Sánh vai nhau dòng huyết quản chảy tràn<br/><br/>Cho đêm tàn trần ải nát vỡ tan <br/>Hồn ngất ngưỡng mênh mang bờ bến mới<br/>Qua đêm vắng bình minh trong vời vợi<br/> Đoản khúc sầu…cắt sợi biệt ly xa... !!!<br/><br/>Trong đêm nay trăng sáng tuyệt cung ngà<br/>Miền khát cháy tan ra dòng sánh bạc<br/>Đêm thưởng ngoạn cảnh tiên hồn đi lạc<br/>Bờ bến ơi !... xao xác một cung thương…&#13;\n      </div>', 'Hồng Dương'),
(61, 'thức một vòng tay', '<div class="col-md-12">&#13;\n        Chiều bên đồi nghe gió thoảng ngàn thông xanh<br/>Cánh bướm gọi thu vương sợi nắng len khăn cổ ấm<br/>Bước chân sơn nữ quyến rũ như hoa dại đại ngàn<br/>Thu đang vào mùa<br/>Âm vang tiếng thác<br/><br/>Đà Lạt chiều vội vàng buông<br/>Sương khói nhớ ai lành lạnh bên hồ<br/>Mắt mơ màng dõi theo từng cánh lá thu rơi<br/>Rượu cần cong cong hương thơm môi nồng<br/>Gió đồi em nghiêng ngả<br/><br/>Thung lũng xanh mây lơ lửng tự tình<br/>Em bâng quơ vươn tay níu đóa dã quỳ <br/>Thu chập chờn trong sắc vàng hoa dại<br/>Anh chập chờn theo những giấc mơ yêu<br/>Thức một vòng tay ấm nồng<br/>                                             Đà Lạt, 02.8.2015&#13;\n      </div>', 'Nguyễn Kim Hương'),
(62, 'mẹ tôi', '<div class="col-md-12">&#13;\n        Mẹ đi qua nắng qua mưa<br/>Mẹ mang sương sớm tay đưa giấc tròn<br/>Lời ru dịu mát tim con<br/>Đường hồng con bước mẹ mòn tóc phai<br/><br/>Gánh gồng trĩu nặng đôi vai<br/>Nuôi con khôn lớn thương hoài tháng năm<br/>Lá vàng rơi nát, xa xăm<br/>Tiếng chiều buông xuống buồn câm vô hình<br/><br/>Một ngày con lớn xa tình<br/>Mặt trời xua bước chân mình đi xa<br/>Mẹ nhìn theo ánh trăng nhòa<br/>Lệ vương tà áo buồn hòa cô đơn<br/><br/>Gió ơi đừng thổi từng cơn<br/>Để rồi mẹ lạnh chập chờn cơn mê<br/>Vòng tay dang rộng bến quê<br/>Một ngày con lại tìm về rời ru.<br/>                                  17-10- 2015&#13;\n      </div>', 'Nguyễn Kim Hương'),
(63, 'chuyện tình người hát rong', '<div class="col-md-12">&#13;\n        (Viết cho người hát rong) <br/>Muốn qua để gửi lời yêu<br/>Ngại bờ xa cách nắng chiều đơn côi<br/>Con đò nghịch nước xa xôi<br/>Nàng là tiên nữ, ta người hát rong<br/><br/>Hát cho qua những long đong<br/>Để rồi năm tháng ngày mong chờ người<br/>Núi trông mây, thật buồn cười<br/>Còn mây trôi mãi cuối trời xa xăm<br/><br/>Hát đi còn những lệ thâm<br/>Giọt tình ta giấu tháng năm héo mòn<br/>Lang thang vẫn một lòng son<br/>Dù nàng không biết ta còn yêu xưa<br/><br/>Lang thang chỉ nắng với mưa<br/>Cất lên tiếng hát cho vừa trái ngang<br/>Nâng ly, tay lại gảy đàn<br/>Bài ca năm tháng trao nàng, yêu câm.  <br/>                          Ngã Bảy, 06-12-2015&#13;\n      </div>', 'Nguyễn Kim Hương'),
(64, '* nhà thơ ...thẩn *', '<div class="col-md-12">&#13;\n        NHÀ THƠ ...THẨN<br/>Thơ : Nguyễn Ngọc René <br/><br/>Không thi sĩ vì là thơ thẩn <br/>Có tài chi thiển cận lời thơ<br/>Anh ba (N3) chữ nghĩa lu mờ<br/>Vài câu xướng họa vui mơ dòng tình<br/>.<br/>Không thi sĩ lung linh câu cú<br/>Vần ,điệu , từ một hủ thông thường<br/>Có chi danh được phô trương<br/>Nhà thơ này nọ xin nhường người hay<br/>.<br/>Chỉ thơ thẩn mỗi ngày sớm tối<br/>Viết nhiều lần thấy tội vần thi<br/>Mang danh để được tiếng gì ?<br/>Ra đi ta có đem chi mà hòng<br/>.<br/>Dòng song thất chỉ mong liên kết<br/>Sau một ngày mỏi mệt vì cày<br/>Gặp nhau gắn bó hôm nay<br/>Vẹn tình thi phú tháng ngày yêu thơ<br/>.<br/>Dẫu vạn dặm xa mờ nhân ảnh<br/>Qua trang Face tựa ánh bình minh<br/>Quây quần bạn hữu thêm tình<br/>Cho thơ đất Việt anh linh ngàn đời...<br/>.<br/>Bạn đã kết không lơi buông thả<br/>Như cánh đồng đầy mạ xanh tươi <br/>Muôn năm thơ Việt sáng ngời<br/>Cùng chung xây đắp thảnh thơi vần tình ...<br/><br/>-N3- Nguyễn Ngọc René - 24/06/2016&#13;\n      </div>', '-N3- Nguyễn Ngọc René'),
(65, 'sóng của lòng ta ru mình', '<div class="col-md-12">&#13;\n        Ru cho nỗi nhớ vào đêm<br/>Ru cho sóng khát môi mềm tình tang<br/><br/>Trăng khuya thao thức lang thang<br/>Con tàu chở những đa mang đi cùng<br/><br/>Lòng giông gió, biển ung dung<br/>Ru cho tàu bớt lạnh lùng khơi xa<br/><br/>Con tàu chở ánh trăng ngà<br/>Có nghe sóng của lòng ta ru minh.<br/><br/>Vũng Tàu, 30.7.2015&#13;\n      </div>', 'Nguyễn Kim Hương'),
(66, 'xin một vé ngày xưa', '<div class="col-md-12">&#13;\n        Cho tôi xin một vé tuổi thơ ơi! <br/>Có hương lúa, tiếng chim, cánh diều ngày xưa thấp thoáng<br/>Có trò chơi cút bắt, có những chiều ngụp lặn sông quê<br/>Để nghe vi vút gió ngoài đồng rộng<br/><br/>Chở cả lời ru<br/>Nuôi lớn tâm hồn<br/>Để nghe sông lắng phù sa<br/>Hoa trái ngọt mượt điệu hò, vọng cổ<br/><br/>Bước chân lênh đênh lạc phố<br/>Có bao giờ quên<br/>Chuyến đò quê ngậm ngùi con sóng cũ<br/>Cơn mưa trái mùa rụng ướt ngày xưa<br/><br/>Xin một vé mình về tuổi nhỏ<br/>Khản giọng trên đồng giữa bát ngát mênh mông<br/>Người men lối mòn thăm con đò nhỏ<br/>Xa cách lâu về từ buổi ấy sang ngang.&#13;\n      </div>', 'Nguyễn Kim Hương'),
(67, 'tháng ba tình nồng', '<div class="col-md-12">&#13;\n        Anh tặng em<br/>một đóa hồng<br/>bảo mình thương mãi<br/>như trong truyện tình<br/><br/>Lòng em<br/>anh vẫn là xuân<br/>mười năm giấc mộng tưởng chừng hôm qua<br/><br/>Cầm tay thoắt<br/>chạm<br/>tháng ba<br/>kề môi anh bảo<br/>em<br/>là đóa thơm<br/><br/>Nâng niu từ thuở dỗi hờn<br/>mắc lên sợi chỉ tơ vương<br/>ngọt ngào<br/><br/>Tình nồng<br/>ta cứ lặng trao<br/>nét duyên đằm thắm<br/>lẫn vào dáng em<br/><br/>Sáng nay<br/>nghe ngọn gió lành<br/>vừa đi qua cửa <br/>môi xuân mỉm cười<br/><br/>Tháng ba ngày tám<br/>anh ơi! <br/>em vui nhận<br/>đóa hoa người<br/>em yêu...<br/><br/>Ngã Bảy, 8.03.2016&#13;\n      </div>', 'Nguyễn Kim Hương'),
(68, 'bến đợi', '<div class="col-md-12">&#13;\n        Bến sông ngày ấy tiễn đưa<br/>Người đi vào chốn gió mưa một chiều<br/>Cuối trời mang cả dấu yêu<br/>Để người ở lại bao nhiêu giọt thầm<br/><br/>Buồn trông con nước xa xăm<br/>Lục bình man mác lang thang nắng chiều<br/>Ngờ đâu chút gió đìu hiu<br/>Chạm vào cõi nhớ, cô liêu đắng lòng<br/><br/>Hoa bần rụng trắng triền sông<br/>Đôi bờ lau sậy nhớ mong thẫn thờ<br/>Bến sông xưa mãi đợi chờ<br/>Ngóng trông người đến bơ vơ một đời...&#13;\n      </div>', 'Nguyễn Kim Hương'),
(69, '50. tôi không thèm chat nữa đâu', '<div class="col-md-12">&#13;\n        Tôi không vào room chat nữa đâu<br/>Vào chat chuốt thêm những thương đau<br/>Tôi mang đến họ nguồn vui mới<br/>Người trả lại tôi vạn chuỗi sầu.<br/><br/><br/>Tác giả : Huỳnh Ngọc Anh Kiệt<br/>Địa chỉ : 211 Nguyễn Trung Trực - TP. Rạch Giá – Kiên Giang<br/>Phone : 0919021688 - 0942874385<br/>Email : vitinh_anhkiet_rg@yahoo.com.vn<br/>Web : www.vitinhanhkiet.com<br/><br/><br/>Tôi như một cánh chim phiêu bạt giữa trời với một vết thương đang nhỏ máu.<br/><br/>Dù đớn đau quằn quại nhưng tôi vẫn tha thiết yêu thương trần gian điên dại này&#13;\n      </div>', 'Huỳnh Ngọc Anh Kiệt'),
(70, '51. ước', '<div class="col-md-12">&#13;\n        Em như dòng suối nhỏ<br/>Reo ca dưới mặt trời<br/>A nh ước thành chiếc lá<br/>Xuôi dòng ra biển khơi.<br/><br/><br/>Em như hoa hồng thắm<br/>Kiêu sa dưới mặt trời<br/>Anh ước thành nắng ấm<br/>Cho sắc hồng thêm tươi.<br/><br/><br/>Em như cánh cò trắng <br/>Vô tư thích rong chơi<br/>Anh ước thành làn gió<br/>Nâng cánh em suốt đời .<br/><br/><br/>Tác giả : Huỳnh Ngọc Anh Kiệt<br/>Địa chỉ : 211 Nguyễn Trung Trực - TP. Rạch Giá – Kiên Giang<br/>Phone : 0919021688 - 0942874385<br/>Email : vitinh_anhkiet_rg@yahoo.com.vn<br/>Web : www.vitinhanhkiet.com<br/><br/><br/>Tôi như một cánh chim phiêu bạt giữa trời với một vết thương đang nhỏ máu.<br/><br/>Dù đớn đau quằn quại nhưng tôi vẫn tha thiết yêu thương trần gian điên dại này&#13;\n      </div>', 'Huỳnh Ngọc Anh Kiệt'),
(71, '52.thói đời đen bạc', '<div class="col-md-12">&#13;\n        Rồi bất chợt túi không còn tiền nữa ,<br/>Xung quanh mình nhìn kỹ chẳng còn ai...<br/>Nhớ ngày xưa có bè bạn lai rai...<br/>Giờ cháy túi không còn ai bên cạnh...<br/><br/><br/>Rồi bất chợt thấy tim mình giá lạnh ,<br/>Giữa cuộc đời hiu quạnh chỉ mình ta ,<br/>Chuyện tình xưa theo năm tháng nhạt nhòa...<br/>Buồn bất chợt thấy em bên kẻ khác...<br/><br/><br/>Rồi bất chợt như cánh chim phiêu bạt ,<br/>Giữa bầu trời ta lạc lõng bơ vơ ...<br/>Bất chợt buồn , bất chợt nhớ bâng quơ <br/>Sầu chất ngất bao niềm thương , nỗi nhớ ...<br/><br/><br/>Tác giả : Huỳnh Ngọc Anh Kiệt<br/>Địa chỉ : 211 Nguyễn Trung Trực - TP. Rạch Giá – Kiên Giang<br/>Phone : 0919021688 - 0942874385<br/>Email : vitinh_anhkiet_rg@yahoo.com.vn<br/>Web : www.vitinhanhkiet.com<br/><br/><br/>Tôi như một cánh chim phiêu bạt giữa trời với một vết thương đang nhỏ máu.<br/><br/>Dù đớn đau quằn quại nhưng tôi vẫn tha thiết yêu thương trần gian điên dại này&#13;\n      </div>', 'Huỳnh Ngọc Anh Kiệt'),
(72, '53.nhớ', '<div class="col-md-12">&#13;\n        Nhớ em thức trắng đêm trường, <br/>Lang thang tìm khắp phố phường bóng em. <br/>Nghe buồn len nhẹ vào tim, <br/>Nghe sầu trĩu nặng cánh chim giữa trời. <br/>Nhớ em nhiều lắm em ơi ! <br/>Thiếu em anh lạc giữa đời … bơ vơ... <br/>Tối nay ảm đạm , trăng mờ, <br/>Cây run , gió khóc , dật dờ tiếng tiêu. <br/>Tìm đâu ra bóng em yêu ? <br/>Để cho vơi bớt ngàn điều nhớ thương...<br/><br/><br/>Tác giả : Huỳnh Ngọc Anh Kiệt<br/>Địa chỉ : 211 Nguyễn Trung Trực - TP. Rạch Giá – Kiên Giang<br/>Phone : 0919021688 - 0942874385<br/>Email : vitinh_anhkiet_rg@yahoo.com.vn<br/>Web : www.vitinhanhkiet.com<br/><br/><br/>Tôi như một cánh chim phiêu bạt giữa trời với một vết thương đang nhỏ máu.<br/><br/>Dù đớn đau quằn quại nhưng tôi vẫn tha thiết yêu thương trần gian điên dại này&#13;\n      </div>', 'Huỳnh Ngọc Anh Kiệt'),
(73, '54. xuân về', '<div class="col-md-12">&#13;\n        Lá thay hoa nở đón xuân về , <br/>Khoe áo trẻ đùa dưới nắng quê. <br/>Nhà ai sum họp vui mừng tết , <br/>Phiêu bạt đời tôi thấy não nề. <br/><br/><br/>Não nề phiêu bạt cuộc đời tôi , <br/>Nhẫm lại hai mươi xuân qua rồi. <br/>Xuân này xuân thứ hai mươi mốt, <br/>Ngoảnh lại trông đời vẫn thế thôi. <br/><br/><br/>Thế thôi xuân này cũng thế thôi , <br/>Cũng vẫn là tôi của năm rồi. <br/>Vẫn áo bạc màu mừng xuân mới , <br/>Chào đón xuân về tôi với tôi.<br/><br/><br/>Tác giả : Huỳnh Ngọc Anh Kiệt<br/>Địa chỉ : 211 Nguyễn Trung Trực - TP. Rạch Giá – Kiên Giang<br/>Phone : 0919021688 - 0942874385<br/>Email : vitinh_anhkiet_rg@yahoo.com.vn<br/>Web : www.vitinhanhkiet.com<br/><br/><br/>Tôi như một cánh chim phiêu bạt giữa trời với một vết thương đang nhỏ máu.<br/><br/>Dù đớn đau quằn quại nhưng tôi vẫn tha thiết yêu thương trần gian điên dại này&#13;\n      </div>', 'Huỳnh Ngọc Anh Kiệt'),
(74, '55. cảm ơn tất cả', '<div class="col-md-12">&#13;\n        Cảm ơn cha , mẹ cho đời con bất hạnh,<br/>Cảm ơn bạn bè ướp lạnh trái tim tôi<br/>Cảm ơn đời ngược đãi ước mơ tôi,<br/>Cảm ơn tất cả những chuỗi ngày lẻ loi , bất hạnh.<br/><br/><br/>Tôi đã biết thế nào là giá lạnh<br/>Cũng hiểu rồi tình đầu rất mong manh<br/>Những ước mơ trong ngày tháng học hành<br/>Đã vỡ vụn tan tành theo mây khói.<br/><br/><br/>Hồi tưởng lại làm trái tim đau nhói<br/>Giận run người nhưng biết nói sao đây<br/>Vì đời tôi hẳn là gió , là mây<br/>Là giọt đắng là vị cay tương lai không có.<br/><br/><br/>Đời rắp tâm quẳng tôi vào một xó<br/>Tôi quyết tâm trong gian khó vươn lên<br/>Vững đôi chân trước bão tố gập ghềnh <br/>Cười nửa miệng trong gieo neo gian khổ. <br/><br/><br/>Tôi chấp nhận một cuộc đời bão tố<br/>Tôi đắp tai trước ngáo ngỗ trần ai<br/>Tôi vui ai biết , ai hay ?<br/>Tôi buồn hồn bạt lưng mây ai ngờ !<br/><br/><br/>Tác giả : Huỳnh Ngọc Anh Kiệt<br/>Địa chỉ : 211 Nguyễn Trung Trực - TP. Rạch Giá – Kiên Giang<br/>Phone : 0919021688 - 0942874385<br/>Email : vitinh_anhkiet_rg@yahoo.com.vn<br/>Web : www.vitinhanhkiet.com<br/><br/><br/>Tôi như một cánh chim phiêu bạt giữa trời với một vết thương đang nhỏ máu.<br/><br/>Dù đớn đau quằn quại nhưng tôi vẫn tha thiết yêu thương trần gian điên dại này&#13;\n      </div>', 'Huỳnh Ngọc Anh Kiệt'),
(75, '56. nỗi sầu bất tận', '<div class="col-md-12">&#13;\n        Anh bật khóc <br/>Vì không còn chỗ đứng <br/>Giữa cuộc đời <br/>Và trong trái tim em <br/>Em đi đi <br/>Theo tiếng gọi con tim <br/>Anh cầu chúc <br/>Em an lành hạnh phúc. <br/>Anh nào ngờ <br/>Mối tình ta kết thúc <br/>Khi con tim <br/>Còn nhỏ máu yêu đương <br/>Em lạnh lùng <br/>Không một chút vấn vương. <br/>Chả có gì <br/>Là lẽ thường cuộc sống... <br/>Trong cuộc đời <br/>Ai mà không kỳ vọng ? <br/>Ở tương lai <br/>Một hạnh phúc tuyệt vời <br/>Rồi mai đây <br/>Mỗi đứa một phương trời <br/>Rồi mai đây <br/>Vùi đời nơi đất lạnh . <br/>Là kết thúc <br/>Một kiếp người bất hạnh <br/>Gió ngưng thổi <br/>Cơn mưa khuya đã tạnh <br/>Giữa cuộc đời <br/>Hiu quạnh chỉ mình anh <br/>Hạnh phúc đơn sơ <br/>Vậy mà cũng tan tành <br/>Chỉ còn lại <br/>Một nỗi sầu bất tận.<br/><br/><br/>Tác giả : Huỳnh Ngọc Anh Kiệt<br/>Địa chỉ : 211 Nguyễn Trung Trực - TP. Rạch Giá – Kiên Giang<br/>Phone : 0919021688 - 0942874385<br/>Email : vitinh_anhkiet_rg@yahoo.com.vn<br/>Web : www.vitinhanhkiet.com<br/><br/><br/>Tôi như một cánh chim phiêu bạt giữa trời với một vết thương đang nhỏ máu.<br/><br/>Dù đớn đau quằn quại nhưng tôi vẫn tha thiết yêu thương trần gian điên dại này&#13;\n      </div>', 'Huỳnh Ngọc Anh Kiệt'),
(76, '57. trái tim nhỏ máu', '<div class="col-md-12">&#13;\n        Em đánh cắp <br/>Trái tim anh nào biết <br/>Em đánh cắp <br/>Linh hồn anh nào hay <br/>Khi bên em <br/>Ôi ! Hạnh phúc ngất ngây <br/>Xa em rồi <br/>Anh nửa say, nửa tỉnh <br/>Anh đã sống <br/>Với chuỗi ngày vô định <br/>Biết về đâu ? <br/>Và dừng lại nơi đâu ? <br/>Khi trái tim <br/>Đang nhỏ máu ưu sầu <br/>Khi tất cả <br/>Đang nhuộm màu nhung nhớ. <br/>Anh vẫn biết <br/>Cuộc đời là tạm bợ <br/>Như đóa hoa <br/>Sớm nở tối tàn phai <br/>Đã yêu em <br/>Anh chấp nhận đắng cay <br/>Anh chấp nhận <br/>Một ngày mai giông gió . <br/>Nếu hạnh phúc <br/>Không phong ba, gian khó <br/>Thì làm sao <br/>Tìm thấy được niềm vui ? <br/>Nếu khi yêu <br/>Trái tim không nhỏ máu <br/>Không khổ đau <br/>Không nhung nhớ xa xôi <br/>Không vết hằn <br/>Rỉ máu ở đôi môi <br/>Thì tình yêu <br/>Đã trở thành sương khói .<br/><br/><br/>Tác giả : Huỳnh Ngọc Anh Kiệt<br/>Địa chỉ : 211 Nguyễn Trung Trực - TP. Rạch Giá – Kiên Giang<br/>Phone : 0919021688 - 0942874385<br/>Email : vitinh_anhkiet_rg@yahoo.com.vn<br/>Web : www.vitinhanhkiet.com<br/><br/><br/>Tôi như một cánh chim phiêu bạt giữa trời với một vết thương đang nhỏ máu.<br/><br/>Dù đớn đau quằn quại nhưng tôi vẫn tha thiết yêu thương trần gian điên dại này&#13;\n      </div>', 'Huỳnh Ngọc Anh Kiệt'),
(77, '58. vẫn mãi yêu em', '<div class="col-md-12">&#13;\n        Em đã tặng anh <br/>Nỗi đau thật tuyệt vời <br/>Em đã tặng anh <br/>Những lời cay đắng nhất <br/>Dù mai đây <br/>Thật sự rơi xuống vực <br/>Anh sẵn sàng <br/>Vì vực thẳm có em . <br/>Đã yêu em <br/>Dù tan nát con tim <br/>Anh cũng vẫn <br/>Mãi yêu không thay đổi <br/>Cho dù mai <br/>Sao dời hay vật đổi <br/>Anh vẫn yêu <br/>Vẫn nghĩ, nhớ về em <br/>Yêu thật lòng <br/>Bằng tất cả trái tim .<br/><br/><br/>Tác giả : Huỳnh Ngọc Anh Kiệt<br/>Địa chỉ : 211 Nguyễn Trung Trực - TP. Rạch Giá – Kiên Giang<br/>Phone : 0919021688 - 0942874385<br/>Email : vitinh_anhkiet_rg@yahoo.com.vn<br/>Web : www.vitinhanhkiet.com<br/><br/><br/>Tôi như một cánh chim phiêu bạt giữa trời với một vết thương đang nhỏ máu.<br/><br/>Dù đớn đau quằn quại nhưng tôi vẫn tha thiết yêu thương trần gian điên dại này&#13;\n      </div>', 'Huỳnh Ngọc Anh Kiệt'),
(78, '59. nhớ về em', '<div class="col-md-12">&#13;\n        Trời chiều thưa nắng mưa buồn hắt hiu <br/>Phố vắng không em nhớ em thật nhiều <br/>Chiều buồn mưa rơi phố vắng<br/>Độc hành thâu đêm thức trắng<br/>Sao lòng hoang vắng<br/><br/><br/>Ngày nào bên em hạnh phúc cuồng say <br/>Để rồi xa nhau nỗi đau đọa đày <br/>Thời gian bên em quá ngắn<br/>Lòng anh đêm nay trống vắng<br/>Nỗi niềm sâu lắng<br/><br/><br/>Phố vắng hôm nay người thưa<br/>Đếm bước đi trong chiều mưa<br/>Giá rét cơn mưa chiều nay<br/>Em nào có hay ?<br/><br/><br/>Chờ đợi bên em từng phút từng giây <br/>Hỡi người yêu ơi thấu cho lòng này<br/>Lạc loài , cô liêu thức trắng <br/>Giọt cà phê đen quá đắng<br/>Đêm buồn nhớ em.<br/><br/><br/>Tác giả : Huỳnh Ngọc Anh Kiệt<br/>Địa chỉ : 211 Nguyễn Trung Trực - TP. Rạch Giá – Kiên Giang<br/>Phone : 0919021688 - 0942874385<br/>Email : vitinh_anhkiet_rg@yahoo.com.vn<br/>Web : www.vitinhanhkiet.com<br/><br/><br/>Tôi như một cánh chim phiêu bạt giữa trời với một vết thương đang nhỏ máu.<br/><br/>Dù đớn đau quằn quại nhưng tôi vẫn tha thiết yêu thương trần gian điên dại này&#13;\n      </div>', 'Huỳnh Ngọc Anh Kiệt'),
(79, '60. trở lại nhà xưa', '<div class="col-md-12">&#13;\n        Điêu tàn xơ xác nhà xưa ,<br/>Cột kèo xiêu vẹo nắng mưa lọt vào...<br/>Trước sân cỏ đã vọt cao <br/>Dĩ nhiên dấu vết ngày nào cũng phai...<br/><br/><br/>Chỉ còn vẳng lại đâu đây ,<br/>Cung đàn lạc điệu với bài ca xưa <br/>" Thương anh nói sao cho vừa... "<br/>Lời ca còn đó người xưa mất rồi !<br/><br/><br/>Nhưng nước vẫn lững lờ trôi ,<br/>Chiếc xuồng còn đó người về nơi đâu ?<br/>Nhớ ai dế nỉ non sầu ?<br/>Cảnh xưa thay đổi nhuộm màu tan thương<br/><br/><br/>Người đi để lại đoạn trường ,<br/>Ta về trông cảnh thê lương não lòng...<br/>Một đời phiêu bạt long đong <br/>Được nhiều nhưng vẫn tay không giữa đời...<br/><br/><br/>Ngăn hoài dòng lệ vẫn rơi...<br/>Không mưa sao lại tả tơi trong lòng !?<br/><br/><br/>Tác giả : Huỳnh Ngọc Anh Kiệt<br/>Địa chỉ : 211 Nguyễn Trung Trực - TP. Rạch Giá – Kiên Giang<br/>Phone : 0919021688 - 0942874385<br/>Email : vitinh_anhkiet_rg@yahoo.com.vn<br/>Web : www.vitinhanhkiet.com<br/><br/><br/>Tôi như một cánh chim phiêu bạt giữa trời với một vết thương đang nhỏ máu.<br/><br/>Dù đớn đau quằn quại nhưng tôi vẫn tha thiết yêu thương trần gian điên dại này&#13;\n      </div>', 'Huỳnh Ngọc Anh Kiệt'),
(80, '151. hà nội chiều thu', '<div class="col-md-12">&#13;\n        Mưa phùng lất phất sương vây<br/>Hồ Gươm gợn sóng … xoay tròn lá bay<br/>Xa xa từng cặp đan tay<br/>Trời se se lạnh màu mây tím buồn<br/><br/>Lăn tăn gợn nước đầu nguồn<br/>Li ti tảo lục bóng chuồn chuồn bay<br/>Đung đưa liễu rũ nhuỵ dài<br/>Lao xao chiếc lá me bay hững hờ<br/><br/>Chiều thu Hà Nội nên thơ<br/>Hắc hiu gió thoảng thẫn thờ bóng ai<br/>Tiếng đàn réo rắc bên tai<br/>Nhạt nhoà nắng tắt ngã dài trên sông<br/><br/>Gọi bầy chim hót thông giông<br/>Quẩy đuôi cá lội ngược dòng Sông Tô<br/>Trãi lòng vạn nẽo hải hồ<br/>Vòng vèo khúc khuỷu Cà Lồ đẹp ghê<br/><br/>Tiếng chuông thanh thót vọng về<br/>Nỉ non lũ dế chân đê não nùng<br/>Chiều thu gợi nhớ mông lung<br/>Ta bơ vơ giữa lạnh lùng chiều thu<br/><br/>Tác giả : Huỳnh Ngọc Anh Kiệt<br/>Địa chỉ : 211 Nguyễn Trung Trực - TP. Rạch Giá – Kiên Giang<br/>Phone : 0919021688 - 0942874385<br/>E mail : vitinh_anhkiet_rg@yahoo.com.vn<br/>Web : www.vitinhanhkiet.com<br/><br/>Tôi như một cánh chim phiêu bạt giữa trời với một vết thương đang nhỏ máu.<br/>Dù đớn đau quằn quại nhưng tôi vẫn tha thiết yêu thương trần gian điên dại này&#13;\n      </div>', 'Huỳnh Ngọc Anh Kiệt'),
(81, 'chú và cháu', '<div class="col-md-12">&#13;\n        Hai chú cháu hồn thơ lấp lánh<br/>Tựa vằng trăng rọi ánh mây bay<br/>Đối thơ độc ẩm đêm ngày<br/>Dâng trào nhựa mật ngất ngây lòng người<br/><br/>Đời khiếm khuyết vẫn tươi như mộng<br/>Hồn thơ bay lồng lộng mênh mông<br/>Khát khao sức sống say nồng<br/>Ước mong tươi đẹp bềnh bồng mây cao<br/><br/>Hồn tỏa sáng như sao bắc đẩu<br/>Thẹn kiếp mây mãi dấu si buồn<br/>Thấm tình vang vọng tiếng chuông<br/>Làm cho trời đất cũng tuôn mưa tình<br/><br/>Hai hồn đẹp lung linh trời rộng<br/>Cháu làm thơ ... chú vọng khúc ca<br/>Tâm trào nhạc lý giao hòa<br/>Ngàn hoa ngơ ngẩn một tòa hồn thơ....<br/><br/>Tặng 2 chú cháu bị liệt  Hoàng Hà và Thuy Linh Lung&#13;\n      </div>', 'Hồng Dương'),
(82, 'tủi thân', '<div class="col-md-12">&#13;\n        Dòng sông chảy qua bao miền mưa nắng<br/>Mồ hôi người mặn đắng một dòng trôi<br/>Lệ âm thầm cày xới nát đôi môi<br/>Ôi đời bậu đơn côi trong ly biệt<br/><br/>Nghe câu hát chiều nay sao da diết<br/>Tiếng tơ lòng như xiết chặt lơi thơ<br/>Trănh trên cao thầm lặng đứng đợi chờ<br/>Thương đời bậu ngẩn ngơ nơi xứ lạ<br/><br/>Chiều buông xuống màn sương che bóng lá<br/>Bậu âm thầm tim rã nát hư hao<br/>Nhớ lời thương ai gợi gắm hôm nào<br/>Tâm xao động hồn lao đao lạc bến<br/><br/>Đêm lặng lẽ bậu  mơ màng  thương mến<br/>Nhớ ngày xưa anh đến ngỏ lời yêu<br/>Như tiếng đàn du mộng cõi mỹ miều<br/>Nay tiếng nhạc tiêu điều ai đang hát....&#13;\n      </div>', 'Hồng Dương'),
(83, '61. khổ vì yêu', '<div class="col-md-12">&#13;\n        Đeo em mấy vạn giờ rồi ? <br/>Biếng ăn, mất ngủ đứng ngồi không yên. <br/>Tại ghen hay tại vì yêu? <br/>Để rồi hờn trách đủ điều về nhau? <br/><br/><br/>Trái sầu càng chất càng cao , <br/>Có trời mới thấu nỗi đau chuyện tình. <br/>" Cuộc đời như một phương trình , <br/>Tìm ra ẩn số bực mình bấy nhiêu " .<br/><br/><br/>Chỉ vì hai chữ :” tình yêu” , <br/>Đời người ngắn ngủi khổ nhiều hơn vui ! <br/>Vạn lần định dạ rút lui , <br/>Nhưng rồi lại cũng ngậm ngùi vì yêu...<br/><br/><br/>Tác giả : Huỳnh Ngọc Anh Kiệt<br/>Địa chỉ : 211 Nguyễn Trung Trực - TP. Rạch Giá – Kiên Giang<br/>Phone : 0919021688 - 0942874385<br/>Email : vitinh_anhkiet_rg@yahoo.com.vn<br/>Web : www.vitinhanhkiet.com<br/><br/><br/>Tôi như một cánh chim phiêu bạt giữa trời với một vết thương đang nhỏ máu.<br/><br/>Dù đớn đau quằn quại nhưng tôi vẫn tha thiết yêu thương trần gian điên dại này&#13;\n      </div>', 'Huỳnh Ngọc Anh Kiệt'),
(84, '62. yêu nhau 1 phút say nồng', '<div class="col-md-12">&#13;\n        Cuộc đời sao quá não nề , <br/>Thói đời đen bạc buồn ơi là buồn... <br/>Trời cao đã sắp sẵn tuồng <br/>Chúng ta chỉ diễn dù buồn hay vui... <br/>Cuộc đời lắm nỗi ngậm ngùi , <br/>Chuyện tình cũng thế rối bời tơ duyên... <br/>Vì tình thất đảo bát điên , <br/>Được chăng cũng chỉ ưu phiền mà thôi ! <br/>Nẻo xưa trường cũ xa xôi... <br/>Bóng xưa hình cũ tan rồi trong mơ... <br/>Cuộc đời như mộng dật dờ , <br/>Cũng như nắng sớm , sương mờ hoàng hôn... <br/>Hoàng hôn nắng tắt sương tan <br/>Chỉ còn bao nỗi sầu mang trong lòng... <br/>Yêu nhau một phút say nồng , <br/>Cố quên hình bóng viễn vong cả đời....<br/><br/><br/>Tác giả : Huỳnh Ngọc Anh Kiệt<br/>Địa chỉ : 211 Nguyễn Trung Trực - TP. Rạch Giá – Kiên Giang<br/>Phone : 0919021688 - 0942874385<br/>Email : vitinh_anhkiet_rg@yahoo.com.vn<br/>Web : www.vitinhanhkiet.com<br/><br/><br/>Tôi như một cánh chim phiêu bạt giữa trời với một vết thương đang nhỏ máu.<br/><br/>Dù đớn đau quằn quại nhưng tôi vẫn tha thiết yêu thương trần gian điên dại này&#13;\n      </div>', 'Huỳnh Ngọc Anh Kiệt'),
(140, 'nhân - quả! (iii) – phật hay ma!', '<div class="col-md-12">&#13;\n        NHÂN - QUẢ! (III) – PHẬT HAY MA!<br/>NP54 - 09/8/2015<br/><br/>. . .<br/>(Câu 1 – Câu 12)<br/>. . .<br/><br/>Mọi chuyện đều có thể xảy ra<br/>Như “chuột sa chĩnh gạo” í a (1)<br/>Ai đã đời - no nê nung núc<br/>Ai tàn đời - hết rúc hết ra<br/><br/>Mọi chuyện đều có thể xảy ra<br/>Như niềm vui bổng vút bay xa<br/>Muộn phiền kia đâu hề da diết<br/>Khi vẫn còn lắm nỗi thiết tha (2)<br/><br/>Mọi chuyện đều có thể xảy ra<br/>Như góc nhìn, tầm ngắm gần xa<br/>Và vận tốc tùy vào vật mốc (3)<br/>Phật hay ma có  ở tại ta (4)<br/><br/>CHÚ THÍCH:<br/>(1) TNVN<br/>(2) “Mọi chuyện đều có thể xảy ra; mọi niềm vui đều có thể mất đi và mọi nỗi muộn phiền đều có thể tìm được cách để an ủi và giảm nhẹ” – (?)<br/>(3) “Thuyết tương đối” – Albert Einstein<br/>(4) Lời Phật dạy. <br/><br/>Nười đăng: TNTNP&#13;\n      </div>', 'NP54'),
(141, 'thức giấc nửa đêm', '<div class="col-md-12">&#13;\n        Đêm nay giấc ngủ chập chờn<br/>Phải chăng lòng lại dỗi hờn không yên<br/>Một mình đò cứ mãi nghiêng<br/>Chênh chao mặt nước niềm riêng âu sầu .<br/><br/>Hỡi tình thơ thẩn nơi đâu ?<br/>Để cho hồn mãi héo sầu đơn côi<br/>Tuổi xuân cứ thế dần trôi <br/>Chỉ còn giữ lại vài lời trong thơ.<br/><br/>Đêm đêm mơ vẫn hoài mơ<br/>Cuộc đời tươi đẹp nên thơ tuyệt vời<br/>Đến khi tỉnh mộng thức rồi<br/>Trong mơ khác thực ngậm ngùi xót xa .<br/><br/>Đời người con gái như hoa<br/>Khéo chăm hương sắc mặn mà giữ lâu<br/>Đời tôi ôm trọn nỗi sầu<br/>Một mình cất giữ nỗi đau trong lòng.<br/><br/>Thơ buồn lắt léo lòng vòng<br/>Duyên tình chưa trọn tơ hồng vội phai<br/>Một mình trên quãng đường dài<br/>Một mình cứ bước mệt nhoài đôi chân .&#13;\n      </div>', 'Trần Thi Thủy'),
(142, 'đêm côi', '<div class="col-md-12">&#13;\n        Trăng kia lơ lửng trên đầu<br/>Còn ta ôm một nỗi sầu trong tim<br/>Tưởng rằng tình đó nằm im<br/>Ngờ đâu thức giấc cho tim mệt hoài.<br/><br/>Trăng côi tưởng nhớ bóng ai<br/>Một mình một bóng đêm dài bơ vơ<br/>Nhìn Trăng ta lại ngẩn ngơ<br/>Tưởng Trăng luôn đẹp nào ngờ như ta .<br/><br/>Một mình Trăng cứ vào ra<br/>Ngày rằm tươi sáng cuối là hắt hiu<br/>Một mình ta cũng buồn thiu <br/>Thấy người bên đó mà nhiều tâm tư.<br/><br/>Ước gì chẳng biết ...giá như..<br/>Thì đâu có phải khư khư nỗi sầu<br/>Dần xa cảm xúc ban đầu<br/>Dần xa khoảng cách hai đầu nhớ thương .<br/><br/>Tưởng rằng tình đó vấn vương<br/>Ngờ đâu lạnh lẽo đêm trường vậy thôi<br/>Có tình tim vẫn mồ côi<br/>Vẫn còn khoảng trống trong đời xót xa.<br/><br/>Biết đâu Người có vì ta<br/>Hay vì một phút bên hoa rồi tàn ?<br/>Biết đâu tình đó muộn màng<br/>Cũng lại nhận được bẽ bàng nào vui .?<br/><br/>Đời là hạnh phúc ngọt bùi..<br/>Là bao cay đắng song song với mình<br/>Thôi ta đừng đợi gì tình<br/>Dù cho tim muốn nén lòng ngủ yên .<br/><br/>Lỡ rồi tình đó hão huyền<br/>Ta xin giữ lại lời nguyền năm nao<br/>Tình ơi xin vẫy tay chào<br/>Yêu làm chi để xanh xao cõi lòng .<br/><br/>Bâng khuâng với dải tơ hồng<br/>Đem đi vùi lấp cho lòng nhẹ vơi<br/>Đêm đêm mắt ngước nhìn trời<br/>Số ông côi cút nên đời tôi lây.<br/><br/>Vui thì ít buồn chất đầy<br/>Thế thôi ta cứ mỗi ngày tình xa<br/>Đời người cũng thế như hoa<br/>Nở ra khoe sắc rồi là tàn phai .<br/><br/>Cuộc đời chẳng ai giống ai<br/>Tim mình mình hiểu, bước sai do mình<br/>Tránh xa hai chữ duyên tình<br/>Ta sẽ không phải một mình buồn đêm .&#13;\n      </div>', 'Trần Thi Thủy'),
(143, 'ta lại xa nhau', '<div class="col-md-12">&#13;\n        Xa nhau rồi Anh có thấy vui không ?<br/>Có nhớ nhung như bao lần đã nói<br/>Có xót xa khi tim mình mong mỏi<br/>Hay chỉ như cơn gió thổi vô tình .?<br/><br/>Xa nhau rồi Anh cứ tìm gái xinh<br/>Cứ nắm tay những cô nào mình thích<br/>Mọi thứ đó Em đâu nhìn ..vô ích<br/>Trái tim Em giờ đã nguội lạnh rồi.<br/><br/>Xa nhau rồi Em vẫn thế mà thôi<br/>Em đã quen sống một mình một bóng<br/>Em đã quen cô đơn và cay đắng<br/>Buồn chút thôi ..mọi thứ lại như đầu .<br/><br/>Xa nhau rồi chắc Anh chẳng buồn đau ?<br/>Bởi thấy Anh vẫn cười vui lắm đó<br/>Lời yêu kia lập tức mang đi ngỏ<br/>Chẳng ngại chi nơi vẫn có người quen .<br/><br/>Xa nhau rồi Em mới được biết thêm<br/>Rằng tình Anh chỉ như mây như gió<br/>Một trái tim chân thành Anh đâu có<br/>Nói lời yêu mà gió lại thổi bay .<br/><br/>Em qua rồi cái tuổi rất thơ ngây<br/>Nên chẳng tin những gì Anh đã nói<br/>Có đôi lúc dại khờ nên bối rối<br/>Xa nhau rồi bình yên lại như xưa .&#13;\n      </div>', 'Trần Thi Thủy'),
(144, 'có nhớ ta?', '<div class="col-md-12">&#13;\n        nhờ gió đến bên khung cửa sổ,<br/>ghé vào bên ấy, nhắn giùm ta,<br/>là: "anh rất nhớ, nhớ em lắm", <br/>rồi hỏi em rằng: "có nhớ ta?"&#13;\n      </div>', 'Xuân Nguyễn (Www.facebook.com/ntxuanpg)'),
(145, '40. cố quên', '<div class="col-md-12">&#13;\n        Mưa hắt hiu <br/>Rơi rơi rơi<br/>Giữa cuộc đời<br/>Ta lạc lõng ....<br/>Một khoảng trống<br/>Ở trong tim<br/>Cố đi tìm<br/>Ngày đã mất...<br/>Mưa lất phất<br/>Bay bay bay<br/>Em là ai ?<br/>Từ đâu đến ?<br/>Mà cố quên<br/>Càng thêm nhớ !<br/>Không duyên nợ<br/>Em đi đi<br/>Làm những gì<br/>Em đã quyết.<br/>Tôi vẫn biết<br/>Lệ lâm ly<br/>Phút phân kỳ<br/>Người ở lại<br/>Lòng tê tái<br/>Đau đau đau<br/>Nhưng chẳng biết <br/>Phải làm sao !<br/>Vì cuộc đời<br/>Nào có khác<br/>Giấc chiêm bao !<br/><br/><br/>Tác giả : Huỳnh Ngọc Anh Kiệt<br/>Địa chỉ : 211 Nguyễn Trung Trực - TP. Rạch Giá – Kiên Giang<br/>Phone : 0919021688 - 0942874385<br/>Email : vitinh_anhkiet_rg@yahoo.com.vn<br/>Web : www.vitinhanhkiet.com<br/><br/><br/>Tôi như một cánh chim phiêu bạt giữa trời với một vết thương đang nhỏ máu.<br/><br/>Dù đớn đau quằn quại nhưng tôi vẫn tha thiết yêu thương trần gian điên dại này&#13;\n      </div>', 'Huỳnh Ngọc Anh Kiệt'),
(146, '41.biển chiều', '<div class="col-md-12">&#13;\n        Đẹp lắm không em biển chiều nay ?<br/>Nhấp nhô sóng giỡn hải âu bay<br/>Nắng chiều hôn nhẹ lên mặt biển<br/>Biển thẹn mặt nàng đỏ tựa say ...<br/><br/><br/>Thấp thoáng thuyền về cuối chân mây<br/>Lờ lững tầng không cánh diều bay<br/>Vi vu tiếng sáo , sương chiều xuống<br/>Sóng biển thì thào trách móc ai ?<br/><br/><br/>Hoàng hôn dần tắt cuối trời xa<br/>Thế là đã hết ... một ngày qua<br/>Hải âu về tổ trông ngày mới<br/>Tắm biển một mình ả Hằng Nga. <br/><br/><br/>Tác giả : Huỳnh Ngọc Anh Kiệt<br/>Địa chỉ : 211 Nguyễn Trung Trực - TP. Rạch Giá – Kiên Giang<br/>Phone : 0919021688 - 0942874385<br/>Email : vitinh_anhkiet_rg@yahoo.com.vn<br/>Web : www.vitinhanhkiet.com<br/><br/><br/>Tôi như một cánh chim phiêu bạt giữa trời với một vết thương đang nhỏ máu.<br/><br/>Dù đớn đau quằn quại nhưng tôi vẫn tha thiết yêu thương trần gian điên dại này&#13;\n      </div>', 'Huỳnh Ngọc Anh Kiệt'),
(147, '42.chiều quê', '<div class="col-md-12">&#13;\n        Líu lo chim hót bờ tre<br/>Đỏ hoe xác phượng , tiếng ve rưng sầu<br/>Bồng bềnh mây trắng về đâu ?<br/>Khói lam nũng nịu , lao xao gió chiều<br/>Mái tranh sơ xác tiêu điều<br/>Trước sân cỏ mọc liêu xiêu gợn buồn<br/>Nghịch nước cô cậu chuồn chuồn<br/>Bính boong xa vọng tiếng chuông nhịp đều<br/>Tầng không lờ lững cánh diều<br/>Te re lũ dế thi kêu não lòng<br/>Tung tăng cá lội ngược dòng<br/>Xà No khúc khỉu xanh trong bóng cò<br/>Khẳng khiu dáng lão đưa đò<br/>Ầu ơ uất nghẹn tiếng hò bên sông<br/>Sương rơi vây kín cánh đồng .<br/>Lúa vàng trải thảm mênh mông lúa vàng ...<br/><br/><br/>Tác giả : Huỳnh Ngọc Anh Kiệt<br/>Địa chỉ : 211 Nguyễn Trung Trực - TP. Rạch Giá – Kiên Giang<br/>Phone : 0919021688 - 0942874385<br/>Email : vitinh_anhkiet_rg@yahoo.com.vn<br/>Web : www.vitinhanhkiet.com<br/><br/><br/>Tôi như một cánh chim phiêu bạt giữa trời với một vết thương đang nhỏ máu.<br/><br/>Dù đớn đau quằn quại nhưng tôi vẫn tha thiết yêu thương trần gian điên dại này&#13;\n      </div>', 'Huỳnh Ngọc Anh Kiệt'),
(148, '43.lời cha dạy', '<div class="col-md-12">&#13;\n        Ngày xưa con bé tí ti <br/>Con nào đâu biết chuyện gì buồn vui<br/>Cha già con trẻ ngậm ngùi<br/>Cảnh cha nghèo khó lui cui suốt ngày<br/>Trong mơ con chắp cánh bay<br/>Để con yên giấc mỏi tay quạt nồng<br/>Thương con cha bế trong lòng<br/>Khi đầu con ấm phập phồng âu lo<br/>Rồi con biết lật biết bò ,<br/>Đứng , đi , chạy, giỡn cha lo trăm điều ...<br/>Những đêm mưa gió hắt hiu<br/>Nhà xiêu , dột nát dế kêu não lòng ...<br/>Một đời vất vả long đong<br/>Nhưng cha cũng vẫn tay không giữa đời <br/>Dạy con giữ đạo làm người<br/>“ Lễ , nghĩa , trí , tín “ suốt đời đừng quên<br/>Đường đời bão tố , gập ghềnh ...<br/>Suốt đời con giữ không quên lời này.<br/><br/><br/>Tác giả : Huỳnh Ngọc Anh Kiệt<br/>Địa chỉ : 211 Nguyễn Trung Trực - TP. Rạch Giá – Kiên Giang<br/>Phone : 0919021688 - 0942874385<br/>Email : vitinh_anhkiet_rg@yahoo.com.vn<br/>Web : www.vitinhanhkiet.com<br/><br/><br/>Tôi như một cánh chim phiêu bạt giữa trời với một vết thương đang nhỏ máu.<br/><br/>Dù đớn đau quằn quại nhưng tôi vẫn tha thiết yêu thương trần gian điên dại này&#13;\n      </div>', 'Huỳnh Ngọc Anh Kiệt'),
(149, '44. hòn tre', '<div class="col-md-12">&#13;\n        Hoàng hôn sương phủ không gian<br/>Mặt trời lặng xuống chói chang nắng chiều<br/>Hòn Tre chẳng thấy tre nhiều<br/>Mít , xoài , dừa , ổi , tiêu , điều vươn cao<br/>Xung quanh sóng biển rì rào<br/>Suối trong mát lạnh đá cao trập trùng<br/>Chim kêu , vượn hú hãi hùng<br/>Hoang sơ giữa biển một vùng trời , mây<br/>Ai đem đá chất ở đây ?<br/>Để biển ghen tức đêm ngày rầm vang .<br/><br/><br/>Tác giả : Huỳnh Ngọc Anh Kiệt<br/>Địa chỉ : 211 Nguyễn Trung Trực - TP. Rạch Giá – Kiên Giang<br/>Phone : 0919021688 - 0942874385<br/>Email : vitinh_anhkiet_rg@yahoo.com.vn<br/>Web : www.vitinhanhkiet.com<br/><br/><br/>Tôi như một cánh chim phiêu bạt giữa trời với một vết thương đang nhỏ máu.<br/><br/>Dù đớn đau quằn quại nhưng tôi vẫn tha thiết yêu thương trần gian điên dại này&#13;\n      </div>', 'Huỳnh Ngọc Anh Kiệt'),
(150, '45.trở lại mo so', '<div class="col-md-12">&#13;\n        Mo So im phất như tờ ,<br/>Trong hang nước đọng bao giờ còn đây ?<br/>Ngút ngàn cây níu áng mây<br/>Cá buồn đớp bóng ngỡ ai cợt đùa<br/><br/><br/>Buốt hồn chợt ngọn gió lùa<br/>Trong hang lạnh buốt như mùa đông sang<br/>Nhà ai khói tỏa mơ màng<br/>Mặt trời tỉnh giấc sương tan mất rồi<br/><br/><br/>Tiếng chim chíu chít xa xôi<br/>Về đây bổng thấy bồi hồi mông lung<br/>Nghiêng trong gió nhẹ thẹn thùng<br/>Hoa khoe sắc đỏ run run mỉm cười<br/><br/><br/>Đồng chua nhưng lúa vẫn tươi<br/>Cây xanh trĩu quả lòng người vẫn vui .<br/><br/><br/>Tác giả : Huỳnh Ngọc Anh Kiệt<br/>Địa chỉ : 211 Nguyễn Trung Trực - TP. Rạch Giá – Kiên Giang<br/>Phone : 0919021688 - 0942874385<br/>Email : vitinh_anhkiet_rg@yahoo.com.vn<br/>Web : www.vitinhanhkiet.com<br/><br/><br/>Tôi như một cánh chim phiêu bạt giữa trời với một vết thương đang nhỏ máu.<br/><br/>Dù đớn đau quằn quại nhưng tôi vẫn tha thiết yêu thương trần gian điên dại này&#13;\n      </div>', 'Huỳnh Ngọc Anh Kiệt'),
(151, '46.tết xa nhà', '<div class="col-md-12">&#13;\n        Q..u..ố..c q.u..ố..c ! Ầu ơ ! Ộp ộp à !<br/>Buồn não buồn nào hơn đêm qua <br/>Ngoài ngoải sao thưa trời tăm tối<br/>Thút thít phòng bên ai nhớ nhà ?<br/><br/><br/>Tui tùi thân tui thì thiếu thốn<br/>Mà má , mà ba ở quá xa .<br/>Anh ảnh lo cho đàn cháu nhỏ<br/>Chị chỉ làm dâu chửa tách nhà<br/><br/><br/>Cô cổ qua đời năm Ất Sửu<br/>Cậu cẩu không còn ở dương gia<br/>Ông ổng đi tu mong theo Phật<br/>Bà bả chăm lo việc tề gia<br/><br/><br/>Cho nển cho nên ai cũng vậy<br/>Quên thẳng quên thằng bé dạy xa<br/>Thút thít phòng bên ai khóc nhỉ ?<br/>Buồn riểng buồn riêng ai biết ta<br/><br/><br/>Ai cũng lò lo về mái ấm<br/>Mòi mỏi mình tui tết xa nhà .<br/><br/><br/>Tác giả : Huỳnh Ngọc Anh Kiệt<br/>Địa chỉ : 211 Nguyễn Trung Trực - TP. Rạch Giá – Kiên Giang<br/>Phone : 0919021688 - 0942874385<br/>Email : vitinh_anhkiet_rg@yahoo.com.vn<br/>Web : www.vitinhanhkiet.com<br/><br/><br/>Tôi như một cánh chim phiêu bạt giữa trời với một vết thương đang nhỏ máu.<br/><br/>Dù đớn đau quằn quại nhưng tôi vẫn tha thiết yêu thương trần gian điên dại này&#13;\n      </div>', 'Huỳnh Ngọc Anh Kiệt'),
(152, '47. cảm xuân', '<div class="col-md-12">&#13;\n        Xuân nay đâu khác những xuân nào <br/>Đã nghèo mạt rệp lại còn đau <br/>Bạn bè nghỉ dạy về nhà tết , <br/>Riêng ta , ta biết tết nơi nao ? <br/><br/><br/>Nơi nao ta biết tết nơi nao ? <br/>Kiếp nghèo xuân đến khổ làm sao ! <br/>Đành thôi bánh mứt chờ xuân khác <br/>Nhắm mắt trông ngày xuân qua mau …. <br/><br/><br/>Qua mau , mong ngày xuân qua mau <br/>Quà tết cháu con hứa năm sau … <br/>Năm sau nghỉ dạy đi buôn cá , <br/>Buôn cá mai ra đổi vận giàu ….<br/><br/><br/>Tác giả : Huỳnh Ngọc Anh Kiệt<br/>Địa chỉ : 211 Nguyễn Trung Trực - TP. Rạch Giá – Kiên Giang<br/>Phone : 0919021688 - 0942874385<br/>Email : vitinh_anhkiet_rg@yahoo.com.vn<br/>Web : www.vitinhanhkiet.com<br/><br/><br/>Tôi như một cánh chim phiêu bạt giữa trời với một vết thương đang nhỏ máu.<br/><br/>Dù đớn đau quằn quại nhưng tôi vẫn tha thiết yêu thương trần gian điên dại này&#13;\n      </div>', 'Huỳnh Ngọc Anh Kiệt'),
(153, '48. ghi lại nơi đây', '<div class="col-md-12">&#13;\n        Ghi lại nơi đây về đời ta đó<br/>Vạn hải hồ với kiếp sống lang thang<br/>Đời lãng du vỗ mạnh cánh chim bàng<br/>Say cờ , điện , mê sáo đàn thi họa ...<br/>Việc trên đời chẳng có gì là lạ<br/>Kiếp phù sinh ta từng trải qua rồi<br/>Lúc non cao trên chóp đỉnh ta ngồi<br/>Khi thủy tận ta lẻ loi cô độc<br/>Nhìn đất nước ta thấy toàn gấm vóc<br/>Nhìn lại mình toàn những mốc cùng meo<br/>Không tham sang ta vui thú kiếp nghèo <br/>Cười nửa miệng trong gieo neo gian khổ <br/>Ta chấp nhận một cuộc đời giông tố<br/>Ta đấu tranh chống ngáo ngỗ trần ai<br/>Ta vui ta ngắm mây bay<br/>Ta buồn hạ bút viết vài câu thơ .<br/>Cuộc đời là mộng, là mơ<br/>Thôi thì cứ để mộng mơ tan tành !<br/><br/><br/>Tác giả : Huỳnh Ngọc Anh Kiệt<br/>Địa chỉ : 211 Nguyễn Trung Trực - TP. Rạch Giá – Kiên Giang<br/>Phone : 0919021688 - 0942874385<br/>Email : vitinh_anhkiet_rg@yahoo.com.vn<br/>Web : www.vitinhanhkiet.com<br/><br/><br/>Tôi như một cánh chim phiêu bạt giữa trời với một vết thương đang nhỏ máu.<br/><br/>Dù đớn đau quằn quại nhưng tôi vẫn tha thiết yêu thương trần gian điên dại này&#13;\n      </div>', 'Huỳnh Ngọc Anh Kiệt'),
(154, '49. chờ tin nhắn', '<div class="col-md-12">&#13;\n        Bạn giận tôi rồi có phải không ?<br/>Tôi đợi nhận tin chỉ một dòng<br/>Thế nhưng càng đợi càng chua xót<br/>Chờ mãi hộp thư vẫn trống không !<br/><br/><br/>Tôi đã làm gì bạn giận tôi ?<br/>Hãy nhắn tin đi ... một dòng thôi<br/>Để rồi trở mặt như xa lạ<br/>Cứ nghĩ trên đời chẳng có tôi !<br/><br/><br/>Tác giả : Huỳnh Ngọc Anh Kiệt<br/>Địa chỉ : 211 Nguyễn Trung Trực - TP. Rạch Giá – Kiên Giang<br/>Phone : 0919021688 - 0942874385<br/>Email : vitinh_anhkiet_rg@yahoo.com.vn<br/>Web : www.vitinhanhkiet.com<br/><br/><br/>Tôi như một cánh chim phiêu bạt giữa trời với một vết thương đang nhỏ máu.<br/><br/>Dù đớn đau quằn quại nhưng tôi vẫn tha thiết yêu thương trần gian điên dại này&#13;\n      </div>', 'Huỳnh Ngọc Anh Kiệt'),
(155, 'lặng', '<div class="col-md-12">&#13;\n        LẶNG<br/><br/>Ái vọng<br/>Giải phóng<br/>Cho nhau<br/>Ai đau?<br/>Ai giết<br/>Tình chết<br/>Ai về<br/>Nhiêu khê<br/>Hứng hết<br/>Đoạn kết<br/>Cuộc tình<br/>Mong manh...<br/><br/>15-03-2016<br/>Nguyên Hữu&#13;\n      </div>', 'Nguyên Hữu'),
(156, 'tiếng gọi', '<div class="col-md-12">&#13;\n        TIẾNG GỌI<br/><br/>Em hãy về thăm mẹ<br/>Thăm quê hương<br/>Thăm lối nhỏ<br/>Con đường...<br/>Thăm ngôi nhà cũ<br/>Căn bếp đó<br/>Ánh lửa đỏ<br/>Bao món ăn mình em thấu tỏ...<br/>Em hãy về<br/>Nằm nghe tiếng mọt kể<br/>Người mẹ già và ngọn đèn dầu<br/>Thức canh thâu<br/>Rưng rưng đôi mắt<br/>Thương con<br/>Nhớ con<br/>Những đứa con xa nhà...<br/>Cuộc sống à<br/>Một phút buông tay<br/>Để em tôi tìm về thăm mẹ<br/>Để bọng mắt đó không phải giữ kìm giọt lệ<br/>Nhạt nhòa...<br/>Em à<br/>Hãy về<br/>Kìa mẹ đang mong...<br/><br/>15-03-2016<br/>Nguyên Hữu&#13;\n      </div>', 'Nguyên Hữu'),
(157, '** thơ facebook **', '<div class="col-md-12">&#13;\n        THƠ FACEBOOK<br/>Thơ : Nguyễn Ngọc René<br/>Thơ miễn phí chỉ đăng FaceBook<br/>Như rơm khô rữa mục ngoài sân<br/>Cho không cũng chả ai cần<br/>Like khen , chế nhạo ai cân được nào ?<br/>.<br/>Lời thơ đến lòng nao cứ viết<br/>Ít nhiều gì tiếng Việt thảo ghi<br/>Vui chơi con chữ tay ghì<br/>Thỏa lòng tâm sự dòng thi hiểu mình<br/>.<br/>Cảm ơn lắm , hoan nghinh độc giả<br/>Đã ghé vào chẳng lạ thăm nhau<br/>Thơ người nhiều vẻ muôn màu<br/>Anh ba (N3) chỉ có trước sau lời thường<br/>.<br/>Dùng song thất hoài hương thi Việt<br/>Chữ trong tâm còn viết vui chơi<br/>Tả mây gió thổi trên trời<br/>Nắng mưa bão lục cho vơi nỗi niềm<br/>.<br/>Rồi đùa giỡn , trang nghiêm cũng có<br/>Ý thơ trao tựa cỏ đồng hoang<br/>Nhặt về xếp lại huy hoàng<br/>Thành câu lẫn cú hò khoang vần tình<br/>.<br/>Nhiều lúc cũng tay rinh bình luận<br/>Biến thành bài lẫn quẩn tìm vui<br/>Mong sao chia ngọt sẻ bùi <br/>Đan tay xa thẳm cùng tui một nhà...<br/>-N3- Nguyễn Ngọc René -,25/06/2016&#13;\n      </div>', '-N3- Nguyễn Ngọc René'),
(158, 'vui', '<div class="col-md-12">&#13;\n        Tôi đi tìm niềm vui....<br/>Niềm vui của tôi nhiều năm về trước<br/>Khi cuộc sống chưa nhiều điều trái ngược<br/>Tôi vô tư hạnh phúc- sống giữa đời<br/>Tôi tìm tôi, một tôi của ngày xưa<br/>Biết yêu thơ, yêu đam mê mãnh liệt<br/>Bao nhiệt huyết bao ước mơ oanh liệt<br/>...Của ngày xưa giờ chết ở đâu rồi<br/>Tôi đi tìm... tìm lại chính tôi<br/>Tìm hạnh phúc ngủ vùi trong lồng ngực<br/>Ngọn lửa hồng xưa kia còn cháy rực<br/>Nay lụi tàn chỉ còn lại... nắm tro...<br/>Ai thổi lửa cho tim tôi- cháy rực<br/>Cho tôi tin vào lẽ phải trên đời<br/>Cho tôi yêu, tôi mơ mộng với đời<br/>Sống vô tư tôi căng tràn sức lực<br/>Cuộc sống ơi, thời gian hãy cho tôi<br/>Tôi tìm lại một tôi đã mất<br/>Tôi sống lại những gì đã chết<br/>Quên niềm đau và bao nỗi khó khăn<br/>Tôi cố gắng tôi sẽ còn cố gắng<br/>Nỗi đau nào khuất phục được tôi<br/>Khi tim tôi còn tình yêu hoang dại<br/>Nhỏ nhoi thôi nhưng bùng cháy giữa đời...<br/>Và tôi sẽ gào thét với trời<br/>Tôi đã tìm được tôi đã mất<br/>Sẽ hoàn thiện cuộc đời đầy chật vật<br/>Yêu và tin cuộc sống cuộc đời&#13;\n      </div>', 'Khù Khờ'),
(159, 'chua xong', '<div class="col-md-12">&#13;\n        hạ đến nắng ươm niềm oi ả<br/>cùng ai lăn lóc giữa cỏ cây<br/>vang vang rên rỉ côn trùng gáy<br/>ôm ấp ngày qua giấc mộng đầy<br/><br/>nhìn cánh hạt bay rồi rơi xuống<br/>loanh quanh rải rác khắp nơi nơi<br/>mùa đi mùa lại lòng thơi thới<br/>xuân hạ thu đông quả tuyệt vời&#13;\n      </div>', 'Quoc Hung Nguyen Cao'),
(160, 'tú bà.', '<div class="col-md-12">&#13;\n        TÚ BÀ.<br/><br/>Mét mét, xanh xanh ,nhợt nước da.<br/>Thân hình mập ú tướng là đà.<br/>Đàn em chục đứa để sai vặt.<br/>Gài gọi mươi cô tiếp khách xa.<br/>Dụ dổ, gái tơ về dập liễu.<br/>Phỉnh lừa trinh nữ để vùi hoa.<br/>Sa chân kỹ viện đành khóc hận.<br/>Lỡ bước Thanh Lậu chẳng dể ra<br/>VH.<br/>VH. XIN TRÂN TRỌNG GIỚI THIỆU CÁC BÀI HỌA CỦA BẰNG HỮU.<br/>*******************************************************<br/>1<br/>Thân gái.<br/>*********<br/>Trâm cài sửa soạn trắng làn da<br/>Má phấn môi son thấy dậm đà<br/>Thân phệ tú bà đeo kính cận<br/>Xác thanh làm gái mắt nhìn xa<br/>Sa chân lõ bước vùi đời liễu<br/>Đắm đuối nghe theo lấp kiếp hoa<br/>Đánh đổi đồng tiền đời đã hét<br/>Bước vào thì dễ khó quay ra....<br/>VANBA65.<br/><br/>2-<br/><br/>Y ĐÊ<br/>Thoạt xem nhờn nhợt cái màu da<br/>Ăn giống chi mà quá đẫy đà*<br/>Bậm trợn dăm thằng ngay dưới trướng<br/>Cô nương mấy chục ở trong nhà<br/>Tám nghề thông thuộc tiêu tùng khách<br/>Bảy chữ đòng đưa rửa nát hoa<br/>Son phấn chi hương sao muốn ói<br/>Vậy mà có lắm kẻ vào ra.<br/>HAIRACHGIA.<br/><br/>3-<br/><br/>Tú Bà Thời Nay.<br/><br/>Tú bà ngày nay thắm màu da<br/>Nương tựa vào quan để tạo đà<br/>Nào phải đong đưa mong tìm khách<br/>Nườm nượp đón đưa tại cửa nhà<br/>Má hồng tiếp thị xinh như mộng<br/>Tiếp viên nghênh đón đẹp như hoa<br/>Quản lý sẵn sàng thay lúc thiếu<br/>Đèn mờ quan chức thấy đầy ra.<br/>PV.<br/>4<br/>VỊNH TÚ BÀ<br/>Bản chất trời sanh ả xấu xa<br/>Hoang dâm nên nghiệp chướng ta bà<br/>Không làm chỉ khoái chơi cho sướng<br/>Rảnh việc thường ham thích xí xoa<br/>Háy nguýt mắt tà kia lúng luyến<br/>Chanh chua miệng cú ấy điêu ngoa<br/>Lầu xanh ngập ngụa mùi nhơ nhớp<br/>SAUMIETVUON.<br/><br/>|:rose:5<br/>Y ĐỀ: TÚ BÀ<br/>(NV – NĐT)<br/><br/>***<br/>Thoa dày phấn bệt phủ làn da…<br/>Vẻ mặt tà gian dáng đẫy đà!<br/>Thể nữ tìm mua người mới lạ…<br/>Cô đầu tiếp đãi kẻ gần xa!<br/>Làm sao để khách còn ưng dạ…<br/>Dọa dẫm cho nàng phải bán hoa!<br/>Khóc cảnh gia đình đau vật vã…<br/>Bao điều cũng Mụ Tú mà ra!!!<br/>***<br/>Ngọc Thụy<br/>---oOo---<br/>6<br/>Y ĐỀ<br/><br/>Phấn phấn son son tái tái da<br/>Lầu xanh quá lứa tướng sa đà<br/>Tú Bà - Lão Giám ngưu tầm mã<br/>Kẻ cắp- bà già hội bán hoa !<br/>Tuyết tuyết đưa tình mời khách tới<br/>Hồng hồng liếc mắt tiễn người xa<br/>Nàng Kiều lỡ bước vào hang ổ <br/>May phúc có người để chuộc ra.<br/>N ĐOAN TUYẾT.<br/>7-<br/>TÚ BÀ<br/><br/>Thớ nợ điêu ngoa mặt Tú bà<br/>Làng chơi hết đát mở hàng hoa<br/>Đầu trâu mặt ngựa cùng vun dựng<br/>Mối lái tà gian dựa vẽ ra<br/>Bán phấn chăn rèn nghề kĩ nữ<br/>Buôn hương nẹt ép chước tà ma<br/>Dòng xoay sóng đẩy Kiều vô chốn<br/>Khốn đốn bao ngày phận xót xa.<br/><br/>*trankhau<br/><br/>8-<br/>TÚ BÀ<br/>Phè phè cái bụng xệ làn da<br/>Công việc vô lương để quá đà<br/>Chuyên bán gái tơ cho kẻ lạ<br/>Lại mua trinh nữ gả người xa<br/>Ngày đêm chà đạp bao da phấn<br/>Năm tháng vùi trôn những mặt hoa<br/>Số phận không may sa kĩ viện<br/>Chim lồng cá chậu thủa nào ra.<br/>TR.Đ.THIÊN.&#13;\n      </div>', 'VUVIETHUNG CÙNG BẰNG HỮU');
INSERT INTO `poem_table` (`poem_id`, `poem_name`, `poem_content`, `poem_author`) VALUES
(161, 'sở khanh.', '<div class="col-md-12">&#13;\n        SỞ KHANH.<br/><br/>Khôi ngô, tuấn tù dáng bô trai.<br/>Thủ đoạn gian manh chẳng kém ai..<br/>Dốt nát lọc lừa vô đạo đức.<br/>Ngu si dối trá lại bất tài.<br/>Vùi liễu dập hoa gây tai tiếng<br/>Gã, bàn gái tơ để có xài.<br/>Thiên hạ , nhìn xem,, là chế diểu..<br/>Mọi người gặp mặt lắm chê bai.<br/>VH <br/>VH XIN TRÂN TRỌNG GIỚI THIỆU CÁC BÀI HỌA CỦA BẰNG HỮU.<br/>*****************************************************<br/><br/>1-<br/>ĐỀ.<br/>******<br/>Tướng tá thông minh rất đẹp trai<br/>Ưa làm tật xấu để chê bai<br/>Luôn mồm tán tỉnh khi đông đúc<br/>Liếc mắt nhìn tìm lúc vắng ai<br/>Gạ gẩm đưa tình tuôn thế giỏi<br/>Gần kề hút nhụy thả thần tài<br/>Mặc đời ghét bỏ không hay biết<br/>Thế sự vui chơi kiếm món xài....<br/>VANBA65.<br/>2-<br/>SỞ KHANH (Y ĐỀ)<br/><br/>Bề ngoài lịch sự bộ dòn trai<br/>Ngọt lành miệng lưỡi nhượng gì ai<br/>Đường bệ mặt mày chiều cao đạo<br/>Thong dong tướng mạo vẻ các đài<br/>Lang sói tim đen nhiều ngược đảo<br/>Gấu hùm dạ độc lắm bi hài<br/>Sở Khanh nổi tiếng tay lừa bạo<br/>Dập nát đời Kiều cả chặng dài.<br/><br/>*trankhau<br/><br/>3<br/>Y ĐỀ<br/><br/>Sở Khanh chết tiếng cả đời trai (1)<br/>Dụ dỗ gái ngoan để có xài<br/>Khăn áo dịu dàng lời có cánh<br/>Thư sinh nho nhã chẳng thua ai<br/>Khua môi múa mép phường vô đạo<br/>Quất ngựa truy phong thế mới tài!<br/>Đâu biết trò đời Kiều mắc bẩy<br/>Đời hoa tan nát kể từ đây (2)<br/>N. ĐOAN TUYẾT.<br/><br/>4-<br/>GÃ SỞ KHANH<br/>bản mặt trông qua cũng điển trai<br/>bề ngoài của gã chẳng thua ai<br/>khua môi miệng hiểm -điêu ngôn giỏi<br/>liếc mắt lòng gian-tính kế tài<br/>đặt bẫy vùi hoa-tham dục vọng <br/>giăng tròng dập liễu -kiếm tiền xài<br/>muôn đời kim cổ người khinh rẻ<br/>họ sở tên khanh mối họa tai.<br/>M.M.TRẮNG.<br/><br/>5-<br/>CHÀNG SỞ KHANH<br/>Tuấn tú khôi ngô dáng đẹp trai<br/>Gian lường diệu kế chã thua ai<br/>Mồm ngon tiếng ngọt khua gian dối<br/>Lưỡi dịu lời êm dụ gạt tài<br/>Dạ thú hồn đen nhiều mẹo ác<br/>Lòng lang ý độc lắm mưu hài<br/>Chàng Khanh nức tiếng điều lường đảo<br/>Khổ phận đời Kiều lắm bẽ bai<br/>GM.Nguyễn Đình Diệm&#13;\n      </div>', 'VUVIETHUNG CÙNG BẰNG HỮU'),
(162, 'thúc sinh.', '<div class="col-md-12">&#13;\n        THÚC SINH.<br/><br/>Đắm sắc thành ra phải lụỵ tình.<br/>Châu Thường chốn ấy có chàng Sinh.<br/>Xa nhà, gặp gái không cần vợ.<br/>Đất khách ,yêu lầm tự khổ mình.<br/>Dại dột, thôi đành cam khó xử.<br/>Ngu khờ dễ chết bởi yêu tinh.<br/>Tham ăn, hốc uống.. nên vưong nợ.<br/>Ái dục ham mê ắt vướng tình.<br/>VH.<br/><br/>VH. XIN TRÂN TRỌNG GIỚI THIỆU CÁC BÀI HỌA CỦA BẰNG HỮU.<br/>********************************************************<br/>1-<br/>Y ĐỀ .<br/>*******<br/>Ham chơi đắm sắc chỉ vì tình<br/>Chốn ấy là chàng đúng thúc Sinh<br/>Hái nhụy nhìn xem vui cả đám<br/>Rờ hoa ngắm nghía thảm riêng mình<br/>Lời ru ngọt diệu vì bầy quỷ<br/>Tiếng hát êm đềm bỡi lũ tinh<br/>Đắm đuối mê sa nên phải lụy<br/>Lòng ưa dạ muốn khổ vì tình...<br/>VANBA65.<br/><br/>2<br/>CHỊU NHỤC HÌNH<br/>Ngẫm lại chuyện xưa của thúc sinh<br/>Lầu xanh chơi gái quá say tình<br/>Ngày ngày bẻ liễu quên thê thiếp<br/>Tháng tháng vùi hoa nhãng vợ mình<br/>Là bụt chở thành loài ác quỷ<br/>Còn người thì biến giống yêu tinh<br/>Hoạn Thư chịu tiếng ghen do bởi...<br/>Để kẻ bán hoa chịu nhục hình.<br/>TR, Đ. THIÊN.<br/><br/>4-<br/><br/>CHÀNG THÚC<br/><br/>Lòng say đã vướng phải men tình<br/>Chuyện của nàng Kiều với Thúc Sinh<br/>Ngỡ tưởng vòng tay nâng đỡ gái<br/>Ngờ đâu rước nạn hại cho mình<br/>Ôm sầu ấy tại bà ma quỷ<br/>Nuốt hận là vì mụ quái tinh<br/>Ngọt miệng cuồng ghen lừa dối khéo<br/>Lòng say đã vướng phải men tình<br/>LƯƠNG TÚ.<br/><br/>44-<br/>CHÀNG THÚC SINH<br/>Có một khách du vướng nợ tình<br/>Kỳ Tâm họ Thúc ấy chàng Sinh<br/>Xa nhà mộ gái đành ôm thảm<br/>Viễn xứ mê người phải khổ mình<br/>Dại dột mang sầu nơi lủ quỷ<br/>Ngơ khờ gánh hận chốn yêu tinh<br/>Ham vui tửu sắc thân phiền lụy<br/>Đắm dục sa mê não bởi tình<br/>GM.Nguyễn Đình Diệm<br/><br/>5-<br/>NGHIỆP BÁO<br/><br/>Dập dìu bướm gặp đóa hoa xinh<br/>Vấn vít không rời chuốc nhục mình<br/>Của lạ ham chơi quên nghĩa vợ<br/>Gái duyên đắm hưởng trót si tình<br/>Trầm mê họ Thúc thành mèo mả<br/>Ghen lộn Hoạn Thư hóa quỉ tinh<br/>Sầu đắng ai ơi cần nhớ kĩ<br/>Tránh xa tục lụy để đời vinh<br/><br/>*trankhau<br/><br/>6-<br/><br/>Y ĐỀ.<br/>Mê sắc làng chơi phải luỵ tình<br/>Đèo bồng dan díu ấy chàng Sinh<br/>Chuộc Kiều công ấy thôi thì nhận<br/>Dối vợ tội này ắt khổ mình<br/>Gió gác trăng thềm yêu hoá dại <br/>Sớm đào tối mận quỷ thành tinh <br/>Một khi họ Hoạn ghen thì biết <br/>Kẻ khóc người vui bởi chữ tình (**)<br/>NG. ĐOAN TUYẾT.<br/>(**)Hoạn Thư bắt Kiều về làm đứa ở, bắt Kiều đánh đàn mừng tiệc đoàn viên của thị và Thúc Sinh<br/><br/>7-<br/>THÚC SINH<br/>đam mê nữ sắc đã vương tình<br/>gian đối đôi đường ấy Thúc Sinh<br/>những tưởng miệng hùm đi nạp thiếp<br/>ngờ đâu gan sứa lụy cho mình<br/>nghĩa nhân đánh mất thành ra quái<br/>chung thủy bỏ rơi đã hóa tinh<br/>ngậm đắng bồ hòn khen vẫn ngọt<br/>muôn đời chịu tiếng chẳng còn vinh.<br/>M.M. TRANG.&#13;\n      </div>', 'VUVIETHUNG CÙNG BẰNG HỮU'),
(163, 'tình xuân cho em.', '<div class="col-md-12">&#13;\n        TÌNH XUÂN CHO EM.<br/>Anh viết tình xuân gởi tặng em.<br/>Ngoài sân hoa nở tận nơi thềm.<br/>Bưóm ong vờn lượn say hương sắc.<br/>Chẳng hiểu sao lòng lại nặng thêm.<br/><br/>Một chút tình xuân vẫn mãi còn.<br/>Em về nơi ấy giữ lòng son<br/>Cuối đời bóng lẽ hồn cô quạnh.<br/>Anh ở nơi nầy dạ héo non.<br/><br/>Anh biết ước mơ mình chẳng tròn.<br/>Duyên tình dang dỡ đắng môi hôn.<br/>Thôi đành phải để cho dòng lệ.<br/>Trôi tận vào tim uất nghẹn hồn.<br/><br/>Thôi nhé em yêu, tình chẳng trọn.<br/>Xin em giữ chặt tấm lòng son.<br/>Xuân về hoa nở đời đang đẹp..<br/>Em hãy vui lên đẹp má hồng..<br/>VH.<br/><br/><br/>XUÂN HOÀI CẢM<br/><br/>Trời xuân trong trẻo, bến xuân xanh<br/>Lộc biếc chồi non lác đác cành<br/>Xuân tình thiếu nữ lên đôi má<br/>Hoa đào cũng thẹn với môi xinh<br/><br/>Ai đã trao tôi những mong manh<br/>Như chút nắng xuân lướt kẽ mành<br/>Còn chút dư hương thời quá vãng<br/>Xin người giữ hộ những ngày xanh<br/><br/>Xuân ơi, xuân có biết cho chăng<br/>Xuân đến rồi đi đã bao lần<br/>Những người năm cũ nay đâu vắng<br/>Sao lòng ta còn mãi bâng khuâng ?<br/>NĐT&#13;\n      </div>', 'VUVIETHUNG CÙNG BẰNG HỮU'),
(164, 'cười ra nước mắt.', '<div class="col-md-12">&#13;\n        CƯỜI RA NƯỚC MẮT.<br/>Sự thật thường hay bị mất lòng.<br/>Cá mè một lứa thế cho xong.<br/>Tai ngơ mắt lấp không hề thấy..<br/>Trí đục tâm mờ chẳng thể trông.<br/>Thế thái nhân tình đừng có nhớ.<br/>Trò đời đểu giã chớ mà mong.<br/>Nhân sinh một kiếp luôn phiền não.<br/>Đâu phài do trời quá bất công.<br/>VH.<br/><br/><br/><br/><br/><br/><br/>VH . XIN TRÂN TRỌNG GIỚI THIỆU CÁC BÀI HỌA CỦA BẰNG HỮU.<br/>******************************************************<br/>1-<br/>Y ĐỀ .<br/>******<br/>Trò đời xảo trá thấy buồn lòng<br/>Tai điếc mắt ngơ thế mới xong<br/>Mặt kẻ nghinh ngang như chẳng thấy<br/>Mồm người cứ nói tựa chưa trông<br/>Nhiều điều gạt gẫm đừng nghe ngóng<br/>Lắm việc ru mơ cũng chớ mong<br/>Thế sự tràn đầy lòng hiểm ác<br/>Tu tâm tích đức sợ hao côn<br/>VANB65.<br/><br/>2-<br/>Làm người không thể có hai lòng<br/>Nặng nợ tang bồng có trả xong?<br/>Cặp mắt mở trừng sao chẳng thấy<br/>Đôi tai sâu hoắm lại không trông<br/>Khoanh tay ngoảnh mặt lòng đếch vướng<br/>Ngậm miệng im hơi dạ chả tường<br/>Vô cảm tàn đời cũng xuống đất<br/>Thì đừng trách kiếp bất cùng công.<br/>SAUMIETVUON.<br/><br/>3-<br/>ĐỠ HOÀI CÔNG<br/><br/>Cười ra nước mắt đủ đau lòng<br/>Dấu biệt riêng mình tưởng sẽ xong<br/>Mệnh số không lành nên gắng đợi<br/>Nhân đời chẳng thuận cứ thèm trông<br/>Đìu hiu cảnh phận bao điều ước<br/>Nhạt nhẽo tâm tình lắm kiểu mong<br/>Hiểu thấu cùng ta ai sớt sẻ<br/>Thôi đành cố chịu đỡ hoài công<br/>LUƠNG TÚ.<br/><br/>4-<br/>BUÔNG ĐI<br/><br/>Sầu não buông đi để nhẹ lòng<br/>Không thì nó lặn mãi vào trong<br/>Sưng gan ruỗng phổi thời bê bết<br/>Loạn óc hao tâm lại nhọc công<br/>Cuộc sống thân này là hạt bụi<br/>Thế gian kiếp ấy tựa giòng sông<br/>Quay đi ngoảnh lại ra mây khói<br/>Vị trí của ta dãy số không .<br/>*trankhau<br/>5- <br/>Bạn tôi<br/><br/>Tôi yêu bạn ấy bởi nơi lòng<br/>Giúp được cho người chẳng nghĩ công <br/>Dạ sáng ơn đền đâu có nghĩ<br/>Tâm hiền nghĩa trả lẽ nào mong<br/>Chiều hôm chén rượu cho là đủ<br/>tảng sáng ly trà vậy vẫn xong<br/>Trẻ cũng như già đều mến mộ<br/>Xa rời một buổi đã hoài trông<br/><br/>thanh Thanh Khiết<br/>11/3/2016&#13;\n      </div>', 'VUVIETHUNG CÙNG BẰNG HỮU'),
(165, 'sống được là vui.', '<div class="col-md-12">&#13;\n        SỐNG ĐƯỢC LÀ VUI.<br/><br/>Có lẽ do ta chẳng phải trời.<br/>Tiền duyên nghiệp báo mãi theo thôi.<br/>Tu tâm sữa tánh làm diều thiện.<br/>Hy vọng kiếp sau sẽ đổi đời.<br/><br/>Xuân vẫn còn đây sắc thắm tươi.<br/>Ngoài kia hoa nở ngập bên trời.<br/>Bướm ong rộn rã chào ngày mới<br/>Đuợc sống là vui chớ ngậm ngùi.<br/><br/>Đã biết đời người chắc khổ thôi.<br/>Sống là trả nợ tất nhiên rồi<br/>Luân hồi nhân quả không thay đổi.<br/>Tự tại an lòng để đưọc vui.<br/><br/>Hãy sống cho ra một kiếp người.<br/>Quên đi buồn khổ để mà vui.<br/>Đứng lên điểm phấn tô son lại.<br/>Ngạo với nhân gian một nụ cười.<br/>VH.<br/><br/><br/>VH. XIN TRÂN TRỌNG GIỚI THIỆU CÁC BÀI HỌA CỦA BẰNG HỮU.<br/>*******************************************************<br/>1-<br/>Y ĐỀ.<br/>*******<br/>Ta luôn lam lủ bỡi xa trời<br/>Cuộc sống chăm lo phải khổ thôi<br/>Cố sát nuôi thân nhiều tội lỗi<br/>Trần gian một kiếp nặng vì đời<br/><br/>Xuân qua thấm thoát hết màu tươi<br/>Nắng rực đổ tuôn nóng đất trời<br/>Ong bướm ngẩn ngơ tìm lối mới<br/>Tránh đi để khỏi phải bùi ngùi<br/><br/>Làm người lắm khổ phải buồn thôi<br/>Một kiếp nhân sinh phải chịu rồi<br/>Tạo hóa luân hồi chưa có đổi<br/>Ráng tu tích đức để tìm vui<br/><br/>Lỡ sinh chịu khổ kiếp làm người<br/>Thù hận quên đi để được vui<br/>Cố gắng vun tình cho ấm lại<br/>Để tìm nhân thế chút vui cười.<br/>VANBA65,<br/><br/>2-<br/>BIẾT MÌNH<br/>Sinh ra được sống bởi ơn trời.<br/>Thiện ác hiền ngay tại bạn thôi.<br/>Tốt xấu sang hèn ta chấp nhận<br/>Giàu nghèo ốm béo có trên đời.<br/>Làm sao phải đạo nên hoàn hảo<br/>Có ích cho người hãy cố thôi.<br/>Chớ vội trèo cao ngoài sức lực<br/>Đừng tham thế tục mới tươi cười<br/>Nguyễn Hùng&#13;\n      </div>', 'VUVIETHUNG CÙNG BẰNG HỮU'),
(166, 'thương nhớ.(song điệp)', '<div class="col-md-12">&#13;\n        THƯƠNG NHỚ.(Song điệp)<br/>Xanh xanh dỏ đỏ miệng môi cười.<br/>Đợi đợi mong mong lệ cứ rơi.<br/>Ân ái dâng trào ân ái dẹp.<br/>Yêu dương tràn ngập yêu đương ngời.<br/>Thao thao, thức thức tròn mơ tưởng.<br/>Trở trở trăn trăn vẹn mộng đời.<br/>Mấy tháng xa nhau mấy tháng nhớ.<br/>Bao lần gặp lại bao lần vui.<br/>VH.<br/><br/><br/>VH. XIN TRÂN TRỌNG GIỚI THIỆU CÁC BÀI HỌA CỦA BẰNG HỮU.<br/>******************************************************<br/>1-.<br/>Y ĐỀ.<br/>*****<br/>Đỏ đỏ hồng hồng nghỉnh mặt cười<br/>Thương thương nhớ nhớ đổ sầu rơi<br/>Ôm ôm ấp ấp mê bừng sáng<br/>Ảm ảm bồng bồng thích rạng ngời<br/>Trở trở trăn trăn nhìn thế sự<br/>Thao thao thức thức ngó trần đời<br/>Bao năm cách biệt bao năm nhớ<br/>Gặp lại bây giờ gặp lại vui...<br/>VANBA65.<br/><br/>2-<br/>Gặp gỡ<br/><br/>Đẹp đẹp xinh xinh ngọt tiếng cười<br/>Ngời trong ánh mắt vẻ tươi ngời<br/>Thương thương nhớ nhớ thì nên giữ<br/>Nghĩa nghĩa tình tình chớ để rơi<br/>Dạ mến thêu mơ còn mến cảnh<br/>Lòng yêu dệt mộng sẽ yêu đời<br/>Tìm đâu dáng ấy tìm đâu nữa<br/>Được sống bên nàng được mãi vui<br/><br/>Thanh Thanh Khiết<br/>16/3/2015<br/>3-<br/>NGÓNG CHỜ<br/><br/>Mọng mọng tơ tơ nhỏ nhẻ cười<br/>Chờ chờ ngóng ngóng mặc mưa rơi<br/>Tình sâu một thuở tình sâu đậm<br/>Nghĩa cả bao năm nghĩa cả ngời<br/>Thiết thiết tha tha bình ổn phận<br/>Da da diết diết mộng tròn đời<br/>Quây quần gặp gỡ quây quần thỏa<br/>Hạnh phúc chan hòa hạnh phúc vui.<br/>LƯƠNG TÚ.<br/><br/>4-<br/>ĐỢI CHỜ<br/>Lấp lánh,mộng mơ nở nụ cười<br/>Mỏi mòn đứng đợi tránh mưa rơi<br/>Ái ân gửi gắm vầng trăng tỏ<br/>Hò hẹn đón đưa ánh nguyệt ngời<br/>Đợi đợi chờ chờ lòng nhớ bạn<br/>Thương thương mến mến dạ yêu đời<br/>Hợp hôn mong ngóng trầu cau đợi<br/>Giây lát bên nhau giây lát vui.<br/>PNam<br/><br/>5-<br/>Chờ chờ đợi đợi dạ làm vui<br/>Tháng tháng ngày ngày nước mắt rơi<br/>Mong nhớ ngút ngàn - ngàn mộng đẹp<br/>Yêu thương nồng thắm - thắm tình đời<br/>Ái ân ân ái xa rồi nhớ<br/>Lưu luyến luyến lưu phải gượng cười<br/>Xa mặt cách lòng- lòng vẫn giữ<br/>Đồng sàng dị mộng mộng tàn thôi.<br/>NĐT&#13;\n      </div>', 'VUVIETHUNG CÙNG BẰNG HỮU'),
(167, 'mong chờ.', '<div class="col-md-12">&#13;\n        MONG CHỜ.<br/>Mảnh trăng khuyết NỮA chưa tròn mộng.<br/>Ước vọng trọn MƯỜI vẫn cứ mong.<br/>BẢY lượt gần nhau duyên mãi thắm.<br/>BA lần xa cách tình còn nồng.<br/>NGÀN thương hãy gởi về bờ đợi.<br/>VẠN nhớ xin trao đến bến trông.<br/>CHÍN tháng u hoài sầu não ruột.<br/>MỘT lời thề hẹn cũng an lòng.<br/>VH.<br/><br/><br/>VH. XIN TRÂN TRỌNG GIỚI THIỆU CÁC BÀI HỌA CỦA BẰNG HŨU.<br/>******************************************************<br/><br/>1-<br/>Y ĐỀ.<br/>*****<br/>Nửa mảnh tình côi tàn ước mộng<br/>Nguyện cầu tám cõi vẫn chờ mong<br/>Ba hôm cách biệt coi sầu thảm<br/>Bảy bữa xa nhau ngóng não nồng<br/>Chín nhớ lao lư luôn ngó đợi<br/>Mười thương quặn thắt mãi nhìn trông<br/>Hai năm luyến ái chưa vừa dạ<br/>Một nửa còn đây chạnh tấm lòng..<br/>Vanba65.<br/><br/>2-<br/>NGUYỆN ƯỚC<br/><br/>Tách NỬA vầng trăng tình vỡ mộng<br/>Chia MƯỜI ngọn sóng bến chờ mong<br/>BA mùa cách biệt còn tươi trẻ<br/>BẢY tháng rời xa vẫn thắm nồng<br/>VẠN buổi âm thầm mưa mãi ngóng<br/>NGÀN ngày lặng lẽ gió luôn trông<br/>CHÍN điều ước nguyện ai từng hứa <br/>MỘT thuở bên nhau thỏa tấm lòng.<br/>LƯƠNG TÚ.<br/><br/>3-<br/>ƯỚC MỘNG<br/>Đỉnh tựa Thái Sơn chưa thỏa mộng<br/>Nước như Đông Hải vẫn hoài mong<br/>Trăm năm huynh đệ tình luôn đượm<br/>Trọn kiếp phu thê nghĩa mãi nồng<br/>Bến cách thuyền xưa giờ phút ngóng<br/>Thuyền xa bến cũ tháng ngày mong<br/>Người đi son sắt bền tâm trí<br/>Kẻ đợi thủy chung vững tấm lòng.<br/>TR.Đ.THIÊN.<br/><br/>4-<br/>Đi tìm …<br/><br/>Đầu xuân gặp cảnh âm thầm mộng<br/>Sớm hạ xa người khắc khoải mong<br/>Bẩy tháng kề bên thương đã nặng<br/>Ba năm cách biệt nhớ luôn nồng<br/>Ngàn nơi biển rộng quê nhà ngóng<br/>Vạn nẻo sông dài đất mẹ trông<br/>Chín vụ trèo non tìm bóng nhạn<br/>Một trăm nỗi khó chẳng buồn lòng<br/><br/>Thanh Thanh Khiết<br/>24/3/2016<br/><br/>5-<br/>YÊN LÒNG<br/>(thể toán thi)<br/>Xin đừng cắt NỬA vầng trăng mộng<br/>Chớ để MƯỜI sao đứng nhớ mong<br/>BẢY chuyến đò sang làn gió quyện<br/>BA làn xe chạy sắc hương nồng<br/>Ngàn lần gưỉ về nơi người đợi<br/>Vạn lượt trao sang chốn bạn trông<br/>Chín bậc tình yêu xin gắng sức<br/>Một đi là đến mới yên lòng.<br/>PNam&#13;\n      </div>', 'VUVIETHUNG CÙNG BẰNG HỮU'),
(168, 'cầu vồng.', '<div class="col-md-12">&#13;\n        CẦU VỒNG.<br/>Trời chiều mây trắng mới bay sang<br/>Ráng ĐỎ vương dầy bóng nắng lan.<br/>Tuổi trẻ đầu XANH thương mãi ngóng.<br/>Người già VÀNG mắt nhớ miên mang<br/>Thân .bồ LỤC Phủ xa lìa cội<br/>Phận liễu LAM Kinh vắng bóng chàng.<br/>Thương lắm dân CHÀM luôn hận tủi.<br/>Đau lòng TÍM ruột đến bầm gan.<br/>VH.<br/><br/><br/>VH XIN TRÂN TRỌNG GIỚI THIỆU CÁC BÀI HỌA CỦA BẰNG HỮU.<br/>*****************************************************<br/>1-<br/>Chàng là màu xanh<br/><br/>Phượng đỏ vươn cành báo hạ sang<br/>Sen vàng rủ bóng cạnh hoàng lan<br/>Thân xim đất tặng khăn lam quấn<br/>Gốc huệ trời ban áo lục mang<br/>Cúc tím nhòa hương nào dám mộng<br/>Hoa chàm nhạt sắc mãi thi gan<br/>Bao màu đã chọn nhưng đâu thể…<br/>Chỉ có hồng xanh sánh với chàng<br/><br/>Thanh Thanh Khiết<br/>27/3/2014.<br/><br/>2-<br/>Y ĐỀ.<br/>******<br/>Mây TRẮNG chiều tà kéo đổ sang<br/>Cầu vòng rực ĐỎ tỏa tràn lan<br/>Rừng XANH ngóng đợi mừng mưa đổ<br/>Biển LỤC lặng im sợ nắng mang<br/>Bông tím lung linh vui dạ thiếp <br/>Lá VÀNG héo úa khổ tâm chàng<br/>Dân CHÀM còn hận nhiều sâu sắc<br/>Ruột TÍM lòng đau uất tận gan...<br/>Vanba65.<br/><br/>3-<br/>BẢY MÀU NHỚ<br/>Gió động mây trời bỗng kéo sang<br/>Xem chừng ĐỎ cả một miền lan<br/>Chờ mưa héo lá rừng XANH ngủ<br/>Đợi nước VÀNG cành suối cạn mang<br/>Sắc LỤC vườn hoa phơi lối ngõ<br/>Màu LAM ngọn khói tỏa thôn chàng<br/>Em là sơn nữ khoe CHÀM áo<br/>Để nhớ cho người TÍM ruột gan<br/>LƯƠNG TÚ.<br/><br/>4-<br/>Y đề<br/><br/>Trở gió mây đen lại kéo sang<br/>Mặt trời ửng đỏ tối dần lan<br/>Hạt mưa lất phất vàng u ám<br/>Giọt nắng lung linh trắng chuyển sang<br/>Bảy sắc xanh màu thu nhỏ lại<br/>Một lòng lục phủ tỏ cùng chàng<br/>Dù em lam lũ trăm ngàn khó<br/>Chẳng để ruột đau phải tím gan !<br/>Đinhquy55.<br/><br/>5-<br/>Y ĐỀ<br/><br/>Chờ mong ĐỎ mắt bóng tình lang<br/>Khắc khoải ai xui thiếp nhớ chàng<br/>XANH ngắt lưng trời chờ bóng nhạn<br/>Võ VÀNG mặt đất ngóng tin sang<br/>Màu LAM dìu dịu mây lơ lửng<br/>Sắc LỤC lung linh nước miên man<br/>Còn nhớ áo CHÀM ngày tiễn biệt<br/>TÍM loang xuân nữ vẫn bền gan !<br/>NGUYỄN ĐOAN TUYẾT&#13;\n      </div>', 'VUVIETHUNG CÙNG BẰNG HỮU'),
(169, 'cảnh chợ chiều.', '<div class="col-md-12">&#13;\n        CẢNH CHỢ CHIỀU.<br/>Ngắm cảnh chợ chiều chán mớ đời.<br/>Bán mua mặc sức thỏa lòng bơi.<br/>Đua chen danh lợi tâm chìm nỗi.<br/>Nhìn thấy thu tàn lệ mãi rơi<br/><br/>Thiên hạ tranh nhau kiếm chổ ngồi.<br/>Ngoài kia con nước mãi đầy vơi.<br/>Bán mua lộn xộn gây nông nỗi.<br/>Cuộc sống người nghèo vẫn khổ thôi.<br/><br/>Ngắm cảnh chợ chiều ngán quá rồi.<br/>Người thì mới tới, kẻ còn ngồi.<br/>Xin đừng , hò hét gây thêm tội.<br/>Thất đức vô nhân quả thật tồi.<br/><br/>Cây cao bóng cả ở đâu rồi.<br/>Sao chẳng còn ai cứu giúp người.<br/>Đem hết tài năng trừ kẻ xấu.<br/>Kỷ cương lập lại giữ yên đời.<br/>VH.<br/><br/>VH . XIN GIỚI THIỆU NHỮNG VẦN THƠ CHIA SẺ ĐÒNG CẢM CỦA BẰNG HỮU.<br/>***************************************************************<br/><br/>1-<br/>Y ĐỀ.<br/>*****<br/>Chợ chiều mua bán để nuôi đời<br/>Đua sức tranh tài cứ mãi bơi<br/>Háo thắng mê tiền không chịu nổi<br/>Tình thương tơi tả ngập sầu rơi<br/><br/>Chen nhau xô đẩy kiếm nơi ngồi<br/>Ngán ngẩm tình đời rớt đổ rơi<br/>Cuộc sống áo cơm gây đến nỗi???<br/>Tranh giành lấn ép đổ tàn thôi<br/><br/>Nhìn xem thấy cảnh ngán nhiều rồi<br/>Chị đứng cô đi cụ mệt ngồi<br/>Chẳng kể tôn ty gây lắm tội<br/>Vô nhân bất đạo lắm điều tồi<br/><br/>Nho gia lễ giáo bỏ đâu rồi<br/>Giáo huấn sao quên chẳng dạy người<br/>Sửa đổi ý đồ mang tật xấu<br/>Tôn ty thượng hạ đẹp vui đời.<br/>VANBA65.<br/>2-<br/><br/>Vỉa hè là để người đi<br/>mang hàng ra bán chỉ vì lợi riêng<br/>kẹt xe tai nạn liền liền<br/>chợ chiều tụ vậy rất phiền đấy nha !<br/>TTK<br/>3-<br/>Mây tím trời xanh cảnh chợ chiều<br/>Tiết thu nắng nhạt gió hiu hiu<br/>Nhấp nhô lều quán người thưa thớt<br/>Hàng hóa vùng quê chẳng có nhiều...<br/>Đinhquy55.<br/>4-<br/>Cây cao bóng cả nghỉ ngơi rồi<br/>Mặc tình thoả sức họ rong chơi<br/>Chồi non cây nhỏ dìu nhau bước<br/>Một mai ấm gốc lại xanh tươi<br/>..........<br/>HXS.<br/><br/>5-<br/><br/>Chợ chiều sao quá nỗi nhiêu khê<br/>Tranh mua giành bán đến ê chề<br/>Vì lẽ "sân si tham" phải thế<br/>Chợ đời ngán ngẩm dạ tái tê<br/>N Đ T.<br/><br/>6-<br/>Ta đứng nhìn cảnh chợ<br/>Thấy lòng buồn dứt day<br/>Cái thời ta ra trận<br/>Có biết đâu thế này<br/><br/>Nói thì hay hay lắm<br/>Làm với nói khác xa<br/>Chỉ hành dân là chính<br/>Sự thực cần nói ra<br/><br/>Vẫn thường nghe nói dối<br/>Vẫn báo cáo báo chồn<br/>Giả dối và xu nịnh<br/>Chợ ơi...mãi trường tồn.<br/>H T ĐÀM.<br/>7-<br/>Chợ chiều lắm nỗi nhiêu khê<br/>Ta về ấm lại tình quê khúc chiều<br/>Quên đời bên chút thương yêu<br/>Cho lòng lắng lại bấy nhiêu ...ấm lòng!!!<br/>Ganhonam.&#13;\n      </div>', 'VUVIETHUNG CÙNG BẰNG HỮU'),
(170, 'ngẫn ngơ.', '<div class="col-md-12">&#13;\n        NGẪN NGƠ.<br/><br/>Em về chốn ấy chẳng mong chờ.<br/>Anh ở nơi nầy ý ngẫn ngơ.<br/>Thao thức đêm trường bao nỗi nhớ.<br/>U hoài ngày tháng những niềm mơ.<br/>Tình xưa vẫn đó lòng xao xuyến.<br/>Nghĩa cũ còn đây dạ thẫn thờ.<br/>Kỹ niệm một thời vương ký ức.<br/>Thương người buổi ấy dệt vần thơ.<br/>VH <br/><br/><br/>VH.XIN TRÂN TRỌNG GIỚI THỆU CÁC BÀI HỌA CỦA BẰNG HỮU.<br/>******************************************************<br/><br/>1-<br/>GIỮ KỸ.<br/>*******<br/>Đã hẹn trông mong mãi đợi chờ<br/>Em sao vắng bóng dạ buồn ngơ<br/>Từng giờ xáo trộn rơi nhung nhớ<br/>Sáu khắc xôn xao đổ mộng mơ<br/>Ngóng cổ nhìn quanh đầy bỡ ngỡ<br/>Nghiêng đầu ngó tới lắm bơ thờ<br/>Anh còn giữ kín tình muôn thuở<br/>Gói kỹ gởi vào những áng thơ..<br/>Vanba65.<br/>2-<br/>NGƠ NGẨN<br/>Khắc khoải lòng anh lúc đợi chờ<br/>Thương nhiều nhớ lắm để hồn ngơ<br/>Những ngày thơ thẩn ôm niềm mộng<br/>Từng tối bơ phờ gợi ước mơ<br/>Mắt dõi đường xưa lòng ngớ ngẩn<br/>Chân qua lối cũ trí ơ thờ<br/>Tình này nghĩa đấy còn vương vấn<br/>Mượn bút giãi bày gửi gắm thơ,<br/>Đ. THIÊN.<br/>3-<br/>NGẪN NGƠ.<br/><br/>Bến cũ giờ đây hẳn đợi chờ<br/>Bao nhiêu kỷ niệm thủa ngu ngơ<br/>Tóc xanh gió thổi ghi niềm nhớ<br/>Chiều xế nắng xiên khắc ước mơ<br/>Bờ lở tình suông còn giữ nghĩa<br/>Đò nghiêng vững dạ vẫn tôn thờ<br/>Bao năm viễn xứ hoài trông ngóng<br/>Một buổi tương phùng gọi ý thơ<br/>......<br/>XS.<br/>4-<br/>TÌNH NGƠ<br/><br/>Lẳng lặng mình ai thổn thức chờ<br/>Đâu người một thuở kết tình ngơ<br/>Rời xa để lại bao niềm mộng<br/>Cách biệt trông hoài những giấc mơ<br/>Chuyện cũ còn ghi dòng mến mộ<br/>Lời xưa vẫn tỏ ý tôn thờ<br/>Bao lần gặp gỡ rồi vương vấn<br/>Trọn mãi trong lòng đẹp áng thơ<br/>LƯƠNG TÚ.<br/><br/>5-<br/>Gửi lại<br/><br/>Đã hẹn đầu thu sẽ đợi chờ <br/>Sao chàng chẳng nhớ lại làm ngơ<br/>Rêu phong quạnh quẽ nơi vườn mộng<br/>Cỏ úa hoang tàn chốn bến mơ<br/>Xót đọng vầng mây trôi lãng đãng<br/>Buồn vương vạt nắng chiếu ơ thờ<br/>Đây dòng tứ tuyệt em vừa viết<br/>Gửi lại cho người một tứ thơ<br/><br/>Thanh Thanh Khiêt<br/>4/4/2016<br/>6-<br/><br/>NHỚ!.<br/>Nhớ lắm người ơi ấy có chờ.<br/>Thương hoài bóng dáng tớ ngu ngơ.<br/>Bao đêm hóng gió nhìn trăng nhớ.<br/>Mấy buổi mưa rơi ngắm hạt mơ.<br/>Đã biết duyên mình như món nợ.<br/>Không đành dứt bỏ lại tôn thờ.<br/>Ôi vì nhật ký qua trang vở.<br/>Cứ vậy từng ngày dệt nét thơ.<br/>NG.HÙNG&#13;\n      </div>', 'VUVIETHUNG CÙNG BẰNG HỮU'),
(171, 'kẻ bất tài.', '<div class="col-md-12">&#13;\n        KẺ BẤT TÀI.<br/><br/>Dốt nát thường hay thích hám danh.<br/>Quyền cao ,chức lớn khoái ham dành<br/>Bạc tiền của cải luôn mong đoạt.<br/>Phú quý giàu sang lại cố tranh.<br/>Mấy lượt sa cơ do thất học.<br/>Bao lần sẩy nghỉệp bởi vô hành.<br/>Tự xưng kẻ cả trong thiên hạ.<br/>Thực chất chuyện chi cũng chẳng rành.<br/>VH.<br/>VH. (Họa bài THAN THAN của anh PH.NG. VĨNH)<br/><br/>THAN THÂN<br/><br/>Cả đời phấn đấu chẳng nên danh<br/>Hỏi lấy đâu ra của để dành<br/>Lục Bát vài câu chưa gột sách<br/>Thơ Đường mấy tứ chửa treo tranh<br/>Nghề chơi múa võ trò không dễ<br/>Nghiệp viết dương oai chuyện khó thành<br/>Tấu sớ cha truyền lưu hậu duệ<br/>Dâng hương cúng giỗ vái cho rành<br/><br/>Phạm Ngọc Vĩnh<br/>VH XIN TRÂN TRỌNG GIỚI THIỆU CÁC BÀI HỌA CỦA BẰNG HỮU.<br/>*******************************************************<br/><br/>1`-<br/>Y ĐỀ .<br/>******<br/>Học hành đổ đạt mới vương danh<br/>Dốt nát quyền cao chớ lấn dành<br/>Chẳng hiểu dùng mưu mong chiếm đoạt<br/>Ngu si cậy sức đợi đua tranh<br/>Sa cơ thất thế như ai phạt<br/>Thất nghiệp bơ vơ tựa kẻ hành<br/>Ngớ ngẩn nhìn đời nào có biết.<br/>Đem thân cuốc đất cũng không rành.....<br/>VANBA65<br/><br/>2-<br/>RÈN TÂM THỂ<br/><br/>Sống khỏe chan hòa giữ tiếng danh<br/>Làm theo nghĩa hiệp chớ ham dành<br/>Âm thầm với bạn quây quần cảnh<br/>Lặng lẽ cùng bầu thưởng thức tranh<br/>Sáng buổi qua bình thơ chửa đạt <br/>Chiều hôm tới luận việc chưa hành<br/>Vui đời hạnh phúc lòng thanh thản<br/>Luyện thể rèn tâm xử trí rành.<br/>LLƯƠNG TÚ.<br/><br/>3-<br/>NGU MÀ TỎ RA NGUY HIỂM<br/>Ngồi lì lợm mãi giữ hư danh<br/>Xú uế người buông nó lại giành<br/>Lợi ích bầy đàn bu chiếm đoạt<br/>A dua lũ nhóm tạo dây tranh<br/>Cán mai suông đuột phường vô học<br/>Lá mít hẹp lòng thứ chả hành<br/>Cô hội lách luồng quân hạ tiện<br/>Gây ra đổ vỡ khổ dân lành!!!<br/>SAUMIETVUON.<br/>4-<br/>ĐEN ĐỎ TRÒ ĐỜI<br/>Con vua ắt lại nối ngôi vua<br/>Quy luật chứ đâu phải chuyện đùa<br/>Cha cọp nào sinh ra chú cún<br/>Bố còng sao đẻ được chàng cua<br/>Chớ ghen số đỏ xơi đầu lợn<br/>Đừng tủi phận đen húp cháo rùa<br/>Tập ấm truyền đời không dễ đổi<br/>Chẳng theo cứ việc tếch vô chùa<br/>QN<br/><br/>5-<br/>HÃY NGẪM<br/>Ở đời lắm kẻ háo ưa danh<br/>Hể có chút thơm vội vả dành<br/>Của cải tiền tài luôn hám đoạt<br/>Quyền cao chức trọng mãi tham tranh<br/>Bao lần thất thế do si đắm<br/>Mấy bận sa cơ bởi lợi hành<br/>Hãy ngẫm suy cùng đời mấy chốc<br/>Trăm năm một thoáng bỏ đi nhanh<br/>GM.Nguyễn Đình Diệm&#13;\n      </div>', 'Vuviethung Cung Be Ban'),
(172, 'nhìn thấy việc quan làm.', '<div class="col-md-12">&#13;\n        NHÌN THẤY VIỆC QUAN LÀM.<br/><br/>Đường dây nóng liệu còn đủ lửa.<br/>Ngày mai nầy nóng nữa hay không ?<br/>Khổ đau cứ mãi chất chồng.<br/>Dân tinh khốn khó mênh mông biển sầu.<br/><br/>Cây lìa cội vì đâu nông nỗi.<br/>Người hại người tội lỗi người ơi.<br/>Quan rồi cũng chỉ một thời.<br/>Thứ dân kia mới là nơi gởi mình.<br/><br/>Sống với nhau trọn tình trọn nghĩa.<br/>Đừng để đời mai mĩa cười chê.<br/>Phải dành một chổ quay về<br/>Cuối đời có một chốn quê yên lành.<br/><br/>Bao nhiêu năm ham danh hám lợi.<br/>Mua giàu sang vời vợi sầu tư.<br/>Bạc vàng châu báu dôi dư.<br/>Ngày lìa cõi tạm cũng như bao người.<br/>VH<br/><br/>VỘI QUĂNG DÙI.<br/><br/>Nỗi khổ con người được mấy vui.<br/>Vì sao đánh trống lại quăng dùi<br/>Tiếng vang vẫn đó mà lùi bước.<br/>Lời vọng còn đây vội rút lui..<br/>Ngọn lửa soi đường chưa đỏ sáng.<br/>Ánh trăng rọi ngõ đã đen thui.<br/>Dân mình gian khó nhiều năm tháng.<br/>Mong sống yên bình chỉ thế thôi.<br/>VH.<br/><br/>VH. XIN TRÂN TRỌNG GIỚI THIỆU BÀI HỌA CỦA CÁC BẰNG HỮU.<br/>------------------------------------------------------<br/>1=CŨNG NHƯ MỌI NGƯỜI.<br/>*******************<br/>Trên trần gian mưa dầu nắng lửa<br/>Hành con người từng bữa trống không<br/>Làm cho vợ phải xa chồng<br/>Mưu sinh viễn xứ mênh mông khổ sầu<br/><br/>Cảnh trần đời nỡ đâu phản bội<br/>Cậy làm quan ép tội cho người<br/>Dân đen khổ lắm trời ơi!<br/>Hết quan ta cũng về nơi dân mình<br/><br/>Sống trên đời có tình có nghĩa<br/>Để đến chừng ngày kia trở về<br/>Lắm lời thiên hạ cười chê<br/>Cuối đường trần thế về quê chẳng lành<br/><br/>Để cho đời công danh quyền lợi<br/>Vùi lấp thân dịu vợi không tư<br/>Trần gian tiếng xấu đầy dư<br/>Xác thân tay trắng cũng như mọi người....<br/><br/>TA BÀ MỘT KIẾP.<br/>****************<br/>Trên đời thấy đẹp mãi đùa vui<br/>Trống vỗ mỏi tay cũng liệng dùi<br/>Héo sức đâu còn mà tiến bước<br/>Tàn hơi đã cạn phải quay lùi<br/>Trăng soi dẫn lối còn trong sáng<br/>Nhựt phủ ngăn đường hết tối thui<br/>Thế sự hăng say gây tội lỗi<br/>Ta bà một kiếp mãn đành thôi.....<br/>VANBA65.<br/><br/>2-<br/><br/>ĐÀNH CHỊU<br/><br/>Dân tình đợi mãi có gì vui<br/>Một lũ mồm to khoắng loạn dùi<br/>Sáng sủa đâu nào buông kiếp lụi<br/>Im lìm vậy đó mặc đời lui<br/>Chờ mong cuộc sống rồi no đủ<br/>Mộng ước tâm lòng khỏi tối thui<br/>Vất vả còn đeo buồn thế sự<br/>Nên đành khổ cực kiếm tiền thôi.<br/><br/>3-ĐÀNH VẬY<br/>ĐỜI NGƯỜI KHỔ CẢNH CÓ CHI VUI<br/>VỖ MIỆNG KHOE MÔI MÃI NẮM DÙI<br/>ĐÁNH TRỐNG OM SÒM RỒI BỎ LỤI<br/>KHUA CHIÊNG RỘNG SỐNG THÊM ĐẦY ĐỦ<br/>KẺ MONG CHÍNH SỰ BỚT TỐI THUI<br/>KHỔ CỰC CÒN ĐEO SẦU NHÂN THẾ<br/>CAM LAO VẤT VẢ KIẾP NGƯỜI THÔI<br/>GM.NGUYỄN ĐÌNH DIỆM&#13;\n      </div>', 'Vuviethung Cung Be Ban'),
(173, 'biển chết.', '<div class="col-md-12">&#13;\n        BIỂN CHẾT.<br/><br/>Từ xưa biển đã rất trong xanh.<br/>Sao lại bây chừ quá mỏng manh.<br/>Chẳng lẻ có người gieo độc tố.<br/>Hay là ai đó ném hôi tanh.<br/>Chức năng vào cuộc điều tra gắp.<br/>Các cấp làm ra rõ ngọn ngành<br/>Những kẻ gian manh đem xữ tội.<br/>Mới mong hả dạ chốn dân lành.<br/>VH.<br/>-------------------------------------------------------<br/>VH. XIN TRÂN TRỌNG GIỚI THIỆU CÁC BÀI HỌA CỦA BẰNG HŨU.<br/>---------------------------------------------------------<br/><br/>1-<br/>Đã hết trung thần.<br/>*****************<br/>Biển đảo biên cương chẳng tốt lành<br/>Tham ăn dính mối bọn lưu manh<br/>Nghe theo bạn tốt đành hôi hám<br/>Nối bước tham quan phải thúi tanh<br/>Đã nhận đầy kho sao sợ khổ<br/>Không còn trống két đã an lành<br/>Thôi đừng đánh đố ai đâu xử<br/>Đã hết trung thần bãi nói ngành.....<br/>VANBA65<br/><br/>2-<br/>KHÔNG GIỮ <br/>Sóng biển ngày đêm vỗ ngọt lành<br/>Hòa dòng nước cuộn chẳng hôi tanh<br/>Làng ngư trĩu lưới vui nghề biển<br/>Hải sản đầy khoang sướng nghiệp ngành<br/>Bỗng dạt ven bờ nhiều cá rữa<br/>Trơ nằm dọc bãi đủ hình manh<br/>Rằng ai thải độc mà không giữ<br/>Sóng biển ngày đêm vỗ ngọt lành<br/>LƯƠNG TÚ<br/><br/>3-<br/>BIỂN CHẾT<br/>Nước biển bao đời vẫn biếc xanh<br/>Ngư dân thả lưới sống an lành<br/>Lỡ làm cá chết xông mùi thối<br/>Đành để cua toi bốc vị tanh<br/>Những kẻ vì tiền tâm ác độc<br/>Hay người hám bạc dạ gian manh<br/>Cơ quan pháp luật điều tra rõ<br/>Từ dưới lên trên đủ các ngành<br/>ĐT.<br/><br/>4-<br/>TẠI AI?<br/>Từ lúc thay màu ấy đỏ xanh<br/>Thì thôi tràn ngập bọn lưu manh<br/>Ngang tàn hủy hoại gieo giông tố<br/>Bạo ngược làm càng rắc thúi tanh<br/>Tổ quốc đang hồi suy gáp gắp<br/>Giang sơn gặp lúc nạn chòng chành<br/>Tay sai bán nước chồng muôn tội<br/>Nước việt còn đâu an với lành!<br/>SAUMIETVUON.&#13;\n      </div>', 'Vuviethung Cung Be Ban'),
(174, 'từ tâm', '<div class="col-md-12">&#13;\n        TỪ TÂM<br/>(Cẩn hoạ bài TÌNH XUÂN của thi sĩ Bửu Truyền)<br/>Lâm nạn nguyện cầu Phật giáng lâm<br/>Tâm hồn nóng nực vẫn từ tâm<br/>Phạm điều nhiều kiếp còn sai phạm <br/>Lầm lỡ một lần vẫn lỗi lầm<br/>Đáo để hành thiền lời kín đáo<br/>Thâm trầm tu tập nghĩa uyên thâm<br/>Nu cười Di Lặc hoa ngàn nụ<br/>Năm tháng trôi dài đến vạn năm<br/>Trần Minh Hiền Orlando ngày 25 tháng 6 năm 2016&#13;\n      </div>', 'Trần Minh Hiền'),
(175, 'hát với tình thương hạnh phúc miên trường', '<div class="col-md-12">&#13;\n        HÁT VỚI TÌNH THƯƠNG HẠNH PHÚC MIÊN TRƯỜNG<br/>Mỗi năm khi mùa Vu lan đến<br/>Lòng con tưởng nhớ đến Cha hiền<br/>Người lặng lẽ âm thầm như ngọn nến<br/>Toả sáng hồn con nâng đỡ hành thiền<br/><br/>Mẹ chăm sóc dạy đàn con hướng thiện<br/>Sương khói trần gian phủ kín mái đầu<br/>Dòng sông chảy qua đời cho nguồn điện<br/>Thiết tha biển khơi ngày tháng nhiệm mầu<br/><br/>Vầng trăng sáng trên bầu trời hiền hậu<br/>Những vì sao lấp lánh muôn đời<br/>Ơn Cha Mẹ ngàn đời không kể thấu<br/>Thiết tha ngàn khơi huyền ảo khung trời<br/><br/>Vu lan đến cho tâm hồn vời vợi<br/>Thắp hương cho Cha, quỳ lạy vô thường<br/>Tặng cho Mẹ đoá hoa hồng tươi mới<br/>hát với tình thương hạnh phúc miên trường<br/>trần minh hiền orlando ngày 5 tháng 8 năm 2014<br/>BETTER<br/>It’s the Vu Lan celebration <br/>I bring my best to give to my mother<br/>I wish all the best for my family <br/>and everything will be wonderful for our universe and always better and better<br/>trần minh hiền orlando August 5, 2014&#13;\n      </div>', 'Trần Minh Hiền'),
(176, 'sao nào rạng rỡ thần tiên', '<div class="col-md-12">&#13;\n        SAO NÀO RẠNG RỠ THẦN TIÊN<br/>Em quay về phố mù sương<br/>nghe tháng hai buồn vô hạn<br/>ngày lễ tình yêu hướng thượng<br/>nụ hồng năm mới miên man<br/><br/>hoa trái chờ xuân dịu dàng<br/>mênh mông một trời thương nhớ<br/>lao xao biển đời lãng mạn<br/>ngọt ngào hương sắc tình thơ<br/><br/>lắc lư thuyền nhỏ xa bờ<br/>long lanh mắt huyền ảo ảnh<br/>trái tim vỡ oà duyên nợ<br/>lạ lùng, sóng sánh, mong manh<br/><br/>cầu mong hạnh phúc yên lành<br/>khát khao nụ cười thánh thiện<br/>đêm nay sao trời lấp lánh<br/>sao nào rạng rỡ thần tiên<br/>trần minh hiền orlando ngày 23 tháng 2 năm 2015<br/>RETRY<br/>I am obsessed by the mysterious stars on the sky<br/>The universe is immense and the time just goes by<br/>Do we have any clue how everything should comply<br/>this is the world where we are living and keeping retry<br/>trần minh hiền orlando February 23, 2015&#13;\n      </div>', 'Trần Minh Hiền'),
(177, 'giấc mộng hè', '<div class="col-md-12">&#13;\n        Hạ đến nắng ươm niềm oi ả<br/>Cùng nhau lăn lóc giữa cỏ cây<br/>Vang vang rên rỉ côn trùng gáy<br/>Ôm ấp ngày qua cõi mơ đầy<br/>Nhìn cánh hạt bay rồi rơi xuống<br/>Loanh quanh rải rác ở mọi nơi<br/>Mùa đi mùa lại lòng thơi thới<br/>Thu đông xuân hạ  quả tuyệt vời<br/>Ai đã tạo nên vũ trụ này ?<br/>Bao la thanh tịnh khắp trời mây<br/>Suối tuôn róc rách tình êm ái<br/>Hơi nóng nung người nắng say say<br/>Vài cánh hoa trôi theo dòng nước<br/>Chuồn chuồn nhấp nhổm khắp đó đây<br/>Chuột chồn lấp ló rời hang hố<br/>Vịt ngan ngúng nguẩy gọi đàn bay<br/>Nhìn em âu yếm tay nắm tay<br/>Đàn ca hương thoảng gió đông tây<br/>Yêu đương ấp ủ bên bờ sậy<br/>Sung sướng nào hơn giấc mộng này !<br/><br/>QHNC&#13;\n      </div>', 'Quoc Hung Nguyen Cao'),
(178, '36. chót lưỡi đầu môi', '<div class="col-md-12">&#13;\n        Nhớ làm chi những gì em đã nói<br/>Lời ngọt ngào , lời chót lưỡi đầu môi ...<br/>Đớn đau nào thời gian cũng quên thôi<br/>Thà như thế còn hơn em lừa dối<br/>Tôi không trách những gì em đã nói<br/>Cũng không buồn lời chót lưỡi đầu môi<br/>Phút qua rồi là giây phút xa xôi<br/>Đừng mặc cảm cho là em có lỗi.<br/>Hai chúng ta là hai người vô tội<br/>Tại cuộc đời, tại số kiếp, thế thôi !<br/><br/><br/>Tác giả : Huỳnh Ngọc Anh Kiệt<br/>Địa chỉ : 211 Nguyễn Trung Trực - TP. Rạch Giá – Kiên Giang<br/>Phone : 0919021688 - 0942874385<br/>Email : vitinh_anhkiet_rg@yahoo.com.vn<br/>Web : www.vitinhanhkiet.com<br/><br/><br/>Tôi như một cánh chim phiêu bạt giữa trời với một vết thương đang nhỏ máu.<br/><br/>Dù đớn đau quằn quại nhưng tôi vẫn tha thiết yêu thương trần gian điên dại nà y&#13;\n      </div>', 'Huỳnh Ngọc Anh Kiệt'),
(179, '37. như dòng sông và chiếc lá', '<div class="col-md-12">&#13;\n        Như dòng sông ta miệt mài đi mãi<br/>Ra biển khơi không quay lại đầu nguồn .<br/>Trong lòng ta chất chứa những ưu buồn ...<br/>Lúc phẳng lặng ... lúc sóng dâng cuồn cuộn...<br/><br/><br/>Như chiếc lá rời cành bay theo gió<br/>Bay về đâu ta chẳng biết về đâu ?!<br/>Một thoáng vui lặng lẽ vỡ tan mau<br/>Chỉ lắng đọng nỗi niềm đau bất tận ...<br/><br/><br/>Tác giả : Huỳnh Ngọc Anh Kiệt<br/>Địa chỉ : 211 Nguyễn Trung Trực - TP. Rạch Giá – Kiên Giang<br/>Phone : 0919021688 - 0942874385<br/>Email : vitinh_anhkiet_rg@yahoo.com.vn<br/>Web : www.vitinhanhkiet.com<br/><br/><br/>Tôi như một cánh chim phiêu bạt giữa trời với một vết thương đang nhỏ máu.<br/><br/>Dù đớn đau quằn quại nhưng tôi vẫn tha thiết yêu thương trần gian điên dại này&#13;\n      </div>', 'Huỳnh Ngọc Anh Kiệt'),
(180, '38. không có em', '<div class="col-md-12">&#13;\n        Không có em cuộc đời buồn vô tận<br/>Mất em rồi sầu đè nặng tim anh ...<br/>Bỡi vì tình yêu như sương khói mong manh<br/>Bỡi mộng ảo không thể thành sự thật<br/><br/><br/>Không nuối tiếc những gì tôi đánh mất<br/>Cũng không buồn vì sự thật phủ phàng<br/>Nếu tình yêu không cách trở trái ngang<br/>Thì chẳng có những vần thơ tôi bán ...<br/><br/><br/>Tác giả : Huỳnh Ngọc Anh Kiệt<br/>Địa chỉ : 211 Nguyễn Trung Trực - TP. Rạch Giá – Kiên Giang<br/>Phone : 0919021688 - 0942874385<br/>Email : vitinh_anhkiet_rg@yahoo.com.vn<br/>Web : www.vitinhanhkiet.com<br/><br/><br/>Tôi như một cánh chim phiêu bạt giữa trời với một vết thương đang nhỏ máu.<br/><br/>Dù đớn đau quằn quại nhưng tôi vẫn tha thiết yêu thương trần gian điên dại này&#13;\n      </div>', 'Huỳnh Ngọc Anh Kiệt'),
(181, '39.tôi vẫn tự do', '<div class="col-md-12">&#13;\n        Tôi vẫn là người , vẫn đường đường chính chính<br/>Vẫn tự do như người khác phải không em ?<br/>Trong người tôi máu vẫn chảy về tim<br/>Sao em cấm trái tim tôi thổn thức ?!<br/>Sao em bảo từ nay anh chấm dứt<br/>Chỉ một dòng tin nhắn gởi cho em ?<br/>Tôi làm sao ngăn cản được con tim ?!<br/>Dù rất muốn ... Dù cố tìm lối thoát ...<br/>Tôi chìm đắm lắng nghe lời chim hót <br/>Hoà quyện mình vào phong cảnh thiên nhiên<br/>Nhìn đàn cá tung tăng cho vơi hết ưu phiền<br/>Nhưng không thể chỉ một ngày tôi quên hết ...<br/>Máu vẫn chảy hiển tôi chưa chết ?<br/>Đừng bắt tôi thành gỗ đá vô tri ...<br/>Em muốn quên em cứ việc quên đi<br/>Tôi tự do nhớ những gì cần nhớ ...<br/><br/><br/>Tác giả : Huỳnh Ngọc Anh Kiệt<br/>Địa chỉ : 211 Nguyễn Trung Trực - TP. Rạch Giá – Kiên Giang<br/>Phone : 0919021688 - 0942874385<br/>Email : vitinh_anhkiet_rg@yahoo.com.vn<br/>Web : www.vitinhanhkiet.com<br/><br/><br/>Tôi như một cánh chim phiêu bạt giữa trời với một vết thương đang nhỏ máu.<br/><br/>Dù đớn đau quằn quại nhưng tôi vẫn tha thiết yêu thương trần gian điên dại này&#13;\n      </div>', 'Huỳnh Ngọc Anh Kiệt'),
(182, 'hạnh phúc ngưỡng chiêm', '<div class="col-md-12">&#13;\n        Hạnh <br/>phúc <br/>ngưỡng <br/>chiêm<br/><br/><br/>Chất ngất tâm giao gió thượng ngàn<br/>Cảm lòng tràn vỡ sóng nguồn lan<br/>Đã say đắm lắm trời  huyền mặc<br/>Còn ước theo vào chốn diệu quang.<br/><br/><br/>Lệ trong mặn chắt giọt tình riêng<br/>Thổn thức châu sa suối chảy hiền<br/>Nhoà ướt không gian mưa ngọc phách<br/>Rỡ ràng quyến luyến Cội Ơn Thiêng.<br/><br/><br/>Hoàn vũ mênh mông muốn ngước nhìn<br/>Bầu trời con mắt dõi đường Tin<br/>Cánh chim đơn chiếc không cô quả<br/>Ngọn gió yêu thưong dưỡng ý tình.<br/><br/><br/>Không xanh giăng mắc sợi tơ lòng<br/>Dấu ấn giao thoa khối vẹn trong<br/>Ánh sáng toả ngời trên vạn vật<br/>Trần gian bừng mở đức cậy trông.<br/><br/><br/>Dã Tràng Cát<br/>Kontum, 26.6.2016&#13;\n      </div>', 'Dã Tràng Cát'),
(183, 'anh chỉ trách anh', '<div class="col-md-12">&#13;\n        Em yêu hỡi sao em nghĩ đơn giản quá<br/>Xa nhau rồi là quên tất cả được sao<br/>Quãng thời gian mà hai đứa từng có nhau<br/>Bao kỷ niệm êm đềm làm em có thể?<br/><br/>Dù đường tình biết chia tay là như thế<br/>Biết cả hai đều cố gắng để mà quên<br/>Nhưng làm sao dễ quên được phút êm đềm<br/>Mắt môi trao và ngập tràn trong mến ái<br/><br/>Anh đã đắm chìm trong tình yêu khờ dại<br/>Đã yêu em và dâng trọn trái tim mình<br/>Anh cứ ngỡ em dành cả tấm lòng trinh<br/>Để giờ anh giật mình tin người quá vội<br/><br/>Những lời đây cũng là những lời sau cuối<br/>Chúc em luôn vui đừng vướng phải âu sầu<br/>Hãy một lần em hãy thử sống vì nhau<br/>Em sẽ hiểu những gì là điều trân trọng<br/><br/>Em sẽ được thấy những niềm vui trong mộng<br/>Chỉ niềm yêu thương và nhung nhớ dâng trào<br/>Em sẽ thấy tình yêu tựa những vì sao<br/>Và vui trong nhau niềm khát khao mong nhớ<br/><br/>Em yêu ơi! giờ ta có còn chi nữa<br/>Trái tim kia giờ cũng đã thuộc về ai<br/>Anh trách anh đã chót lựa chọn sai<br/>Sai tất cả ngay từ buổi đầu em đến<br/><br/>Em hãy cứ vui bên người mình thương mến<br/>Anh không còn là ngọn nến của đời em.<br/><br/>HD ngày 23 tháng 06 năm 2016&#13;\n      </div>', 'Nguyễn Quang Long'),
(184, 'thành phố tôi yêu', '<div class="col-md-12">&#13;\n        Hải Phòng ơi thành phố của tôi yêu<br/>Biển rộng bao la với chiều gió lộng<br/>Những non cao ngàn đời vững bóng<br/>Những con Tàu lướt sóng giữa trùng khơi<br/><br/>Đàn hải âu bay lượn phía chân trời<br/>Có bến Cảng muôn nơi thầm mơ tưởng<br/>Biển Đồ Sơn biết bao người tận hưởng<br/>Bao con người vui sướng ngắm hoàng hôn<br/><br/>Biển về đêm, cảnh đẹp đến mê hồn<br/>Đây Đảo Ngọc du hồn theo nhịp thở<br/>Đảo Cát Bà Khách xa gần luyến nhớ<br/>Cảng Hải Phòng mang tầm cỡ truyền nhân<br/><br/>Sắc phượng hồng như trao gửi ái ân<br/>Thành phố cảng vẫn bao đời quy củ<br/>Dẫu bão tố hay mưa giông vần vũ<br/>Vẫn hiên ngang giữ nguyên vẹn tình quê<br/><br/>Dù cho ai có đi khắp bốn bề<br/>Lòng vẫn mãi nhớ về thành phố cảng.<br/><br/>HD ngày 25 tháng 06 năm 2016&#13;\n      </div>', 'Nguyễn Quang Long'),
(185, 'vẹn tình', '<div class="col-md-12">&#13;\n        Đôi mình gặp lại giữa ngàn mai <br/>Thoả ước ngày nao nhớ miệt mài<br/>Nghĩa cả muôn đời không nhạt úa<br/>Ân nồng trọn kiếp chẳng mờ phai<br/>Tô màu cuộc sống hoài tươi đẹp<br/>Điểm nét tình duyên mãi thắm dài<br/>Kỷ niệm ban đầu ghi khắc giữ<br/>Êm đềm hạnh phúc rạng ngời khai.<br/><br/>HD ngày 26 tháng 06 năm 2016&#13;\n      </div>', 'Nguyễn Quang Long');
